import React, { useState, useEffect } from "react";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiSun,
  FiMoon,
} from "react-icons/fi";
import { FaDocker, FaPython, FaReact } from "react-icons/fa";
import { SiJenkins, SiPostman } from "react-icons/si";
import { motion } from "framer-motion";

export default function App() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) document.documentElement.classList.add("dark");
    else document.documentElement.classList.remove("dark");
  }, [darkMode]);

  // Highlight your projects with emojis, focus on Python automation & Docker
  const projects = [
    {
      title: "🐍 Python Hybrid Automation Framework",
      period: "May 2025 - Jun 2025",
      descPoints: [
        "Built scalable automation using Selenium WebDriver & Python.",
        "Implemented Page Object Model (POM) for clean, reusable code.",
        "Data-driven tests using pytest & CSV/JSON files for robustness.",
        "Parallel runs with pytest-xdist ⚡ & cross-browser support.",
        "Integrated CI/CD pipelines with Jenkins & Docker containerized tests.",
      ],
      techStack: ["Python", "Selenium", "pytest", "POM", "Jenkins", "Docker"],
      repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
    },
    {
      title: "⚙️ API Automation Framework",
      period: "Aug 2025 - Aug 2025",
      descPoints: [
        "Automated REST API validation with pytest & requests.",
        "Schema validation 📜 to ensure API response correctness.",
        "Extensive positive & negative tests for reliability.",
        "Generated HTML test reports 📝 & integrated with Jenkins CI.",
        "Version control & collaboration using GitHub.",
      ],
      techStack: ["Python", "pytest", "Requests", "JSON Schema", "Jenkins"],
      repo: "https://github.com/iamajjayya/api-automation",
    },
    {
      title: "🛒 E-commerce Website",
      period: "Live (React.js)",
      descPoints: [
        "Built a responsive e-commerce platform with React.js.",
        "User authentication, product listing, cart & checkout flow.",
        "Focus on UI/UX for seamless shopping experience.",
        "Live site: ",
        <a
          key="ecomlink"
          href="https://ecommerceajjayya.netlify.app/"
          target="_blank"
          rel="noreferrer"
          className="text-orange-600 hover:underline"
        >
          ecommerceajjayya.netlify.app
        </a>,
      ],
      techStack: ["React.js", "REST API", "Authentication", "UI/UX"],
      repo: null,
    },
    {
      title: "🏋️ Gym Workout App",
      period: "React.js & YouTube API",
      descPoints: [
        "Workout guide with ExerciseDB & YouTube API integration.",
        "Dynamic workout recommendations with rich video content.",
      ],
      techStack: ["React.js", "REST API", "YouTube API"],
      repo: "https://github.com/iamajjayya/gym-app",
    },
  ];

  const skills = [
    { category: "🧪 Testing", items: ["Manual Testing (1 year experience)", "Functional & Regression", "Sanity & Smoke", "Cross-Browser Testing", "Bug Reporting"] },
    { category: "🤖 Automation", items: ["Python (Expert)", "Selenium WebDriver", "pytest", "POM Design", "API Testing with Requests", "pytest-html reporting"] },
    { category: "🛠️ Tools & Tech", items: ["Docker 🐳", "Git/GitHub", "Jenkins CI/CD", "Postman", "Jira", "React.js", "SQL", "HTML/CSS/JavaScript"] },
  ];

  const experience = [
    {
      role: "QA/QC Engineer",
      company: "RTBDemand.com",
      period: "Sep 2024 – Present (Remote)",
      points: [
        "Performed manual & exploratory testing on ad-tech platforms.",
        "Verified ad script integration, placement & refresh cycles.",
        "Executed sanity, regression & smoke tests with detailed reports.",
        "Collaborated with developers to resolve bugs & optimize delivery.",
        "Basic API validation for ad delivery & monitoring.",
      ],
    },
    {
      role: "Front-End Developer Intern",
      company: "Internplus+",
      period: "Jul 2024 – Sep 2024 (Remote)",
      points: [
        "Developed responsive UI components using React.js.",
        "Integrated and tested APIs in Agile sprints.",
      ],
    },
    {
      role: "Data Analyst (Part-time)",
      company: "Dhi Innovations",
      period: "May 2023 - Jul 2023",
      points: ["Supported data-driven operations and reporting."],
    },
    {
      role: "Operation Executive & Catalogue Data Analyst (Internship)",
      company: "Zeitgeist Retail Pvt. Ltd.",
      period: "Dec 2023 - May 2024",
      points: ["Managed catalogue data & supported retail operations."],
    },
  ];

  const education = [
    { degree: "MCA", institute: "JAIN University", period: "2023–2025" },
    { degree: "BCA", institute: "Administrative Management College", period: "2019–2022", cgpa: "8.38" },
    { degree: "PUC", institute: "Loyola Composite PU College", period: "2017–2019", percentage: "90.4%" },
    { degree: "SSLC", institute: "Loyola Composite School", period: "2016–2017", percentage: "64.48%" },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-tr from-teal-50 to-purple-100 dark:from-gray-900 dark:to-purple-900 transition-colors duration-700 font-sans">
      <div className="max-w-6xl mx-auto px-6 lg:px-12 py-12 text-gray-900 dark:text-gray-100">
        {/* Dark Mode Toggle */}
        <div className="flex justify-end mb-6">
          <button
            onClick={() => setDarkMode((d) => !d)}
            aria-label="Toggle Dark Mode"
            className="p-2 rounded-full bg-purple-600 text-white hover:bg-purple-700 transition"
          >
            {darkMode ? <FiSun size={22} /> : <FiMoon size={22} />}
          </button>
        </div>

        {/* Header */}
        <motion.header
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="flex flex-col sm:flex-row items-start sm:items-center justify-between mb-14 gap-6"
        >
          <div>
            <h1 className="text-5xl font-extrabold bg-gradient-to-r from-purple-600 via-pink-500 to-orange-400 bg-clip-text text-transparent select-text leading-tight">
              Ajjayya G V <br />
              <span className="text-xl font-semibold text-gray-700 dark:text-gray-300">
                QA & Python Automation Tester
              </span>
            </h1>
            <p className="mt-3 text-lg font-medium select-text">
              📍 Electronic City, Bengaluru &nbsp;&nbsp;|&nbsp;&nbsp; 📅 05-Apr-2002 <br />
              📞 6366325113 &nbsp;&nbsp;|&nbsp;&nbsp; 📧 ajjayya2002@gmail.com
            </p>
          </div>
          <motion.a
            href="/resume.pdf"
            target="_blank"
            rel="noopener noreferrer"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            className="inline-flex items-center gap-3 px-6 py-3 bg-gradient-to-r from-purple-600 to-orange-400 text-white rounded-xl shadow-lg font-semibold tracking-wide shadow-orange-400/50"
          >
            <FiDownload size={20} /> Download Resume
          </motion.a>
        </motion.header>

        {/* Profile Summary */}
        <motion.section
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2, duration: 0.7 }}
          className="bg-white dark:bg-gray-800 rounded-3xl p-8 shadow-lg mb-14"
        >
          <h2 className="text-3xl font-bold text-purple-700 dark:text-purple-400 mb-4 select-text">
            👋 Profile Summary
          </h2>
          <p className="text-gray-800 dark:text-gray-200 text-lg leading-relaxed select-text">
            I’m a <strong>detail-driven QA Tester</strong> with <span className="text-purple-600 font-semibold">1 year</span> of solid manual testing experience and a <span className="text-purple-600 font-semibold">strong expertise in Python automation</span>. I build robust automation frameworks and APIs with clean, maintainable code to ensure quality at speed. Passionate about bridging manual and automation testing for full coverage and continuous delivery.
          </p>
        </motion.section>

        {/* Skills */}
        <section className="mb-14">
          <h3 className="text-3xl font-extrabold mb-8 text-purple-700 dark:text-purple-400 select-text">
            🛠️ Skills & Tools
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            {skills.map(({ category, items }) => (
              <div key={category} className="bg-white dark:bg-gray-800 rounded-2xl p-6 shadow-md">
                <h4 className="text-xl font-bold mb-5 text-purple-600 dark:text-purple-400 select-text">
                  {category}
                </h4>
                <ul className="list-disc list-inside space-y-2 text-gray-700 dark:text-gray-300 select-text">
                  {items.map((skill) => (
                    <li key={skill} className="text-lg font-medium">
                      {skill.includes("Docker") ? (
                        <>
                          <FaDocker className="inline text-blue-500 mr-1" /> {skill}
                        </>
                      ) : skill.includes("Python") ? (
                        <>
                          <FaPython className="inline text-yellow-500 mr-1" /> {skill}
                        </>
                      ) : skill.includes("React") ? (
                        <>
                          <FaReact className="inline text-cyan-500 mr-1" /> {skill}
                        </>
                      ) : skill.includes("Jenkins") ? (
                        <>
                          <SiJenkins className="inline text-red-600 mr-1" /> {skill}
                        </>
                      ) : skill.includes("Postman") ? (
                        <>
                          <SiPostman className="inline text-orange-500 mr-1" /> {skill}
                        </>
                      ) : (
                        skill
                      )}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </section>

        {/* Projects */}
        <section className="mb-14">
          <h3 className="text-3xl font-extrabold mb-10 text-purple-700 dark:text-purple-400 select-text">
            🚀 Projects
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
            {projects.map(({ title, period, descPoints, techStack, repo }) => (
              <motion.article
                key={title}
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                whileHover={{ scale: 1.05, boxShadow: "0 15px 30px rgba(156, 75, 230, 0.3)" }}
                transition={{ duration: 0.4 }}
                className="bg-white dark:bg-gray-800 rounded-3xl p-7 shadow-lg cursor-pointer flex flex-col"
              >
                <div className="flex justify-between items-center mb-4">
                  <h4 className="text-2xl font-bold text-purple-700 dark:text-purple-400 select-text">{title}</h4>
                  {period && (
                    <span className="text-sm font-semibold text-gray-500 dark:text-gray-400 select-text">
                      {period}
                    </span>
                  )}
                </div>
                <ul className="text-gray-700 dark:text-gray-300 list-disc list-inside mb-5 select-text space-y-1">
                  {descPoints.map((pt, idx) => (
                    <li key={idx}>{pt}</li>
                  ))}
                </ul>
                <div className="flex flex-wrap gap-2 mb-4">
                  {techStack.map((tag) => (
                    <span
                      key={tag}
                      className="text-xs px-3 py-1 rounded-full bg-purple-100 dark:bg-purple-900 text-purple-700 dark:text-purple-300 font-semibold select-text"
                    >
                      {tag}
                    </span>
                  ))}
                </div>
                {repo && (
                  <a
                    href={repo}
                    target="_blank"
                    rel="noreferrer"
                    className="inline-flex items-center gap-2 text-purple-600 dark:text-purple-400 font-semibold hover:underline select-text"
                  >
                    View Repo <FiGithub size={18} />
                  </a>
                )}
              </motion.article>
            ))}
          </div>
        </section>

        {/* Experience */}
        <section className="mb-14">
          <h3 className="text-3xl font-extrabold mb-10 text-purple-700 dark:text-purple-400 select-text">
            💼 Professional Experience
          </h3>
          <div className="space-y-8 max-w-4xl mx-auto">
            {experience.map(({ role, company, period, points }, idx) => (
              <article
                key={idx}
                className="bg-white dark:bg-gray-800 rounded-3xl p-7 shadow-lg"
              >
                <h4 className="text-xl font-bold text-purple-700 dark:text-purple-400 mb-1 select-text">
                  {role} @ <span className="font-normal">{company}</span>
                </h4>
                <p className="text-sm font-semibold text-gray-500 dark:text-gray-400 mb-4 select-text">
                  {period}
                </p>
                <ul className="list-disc list-inside text-gray-700 dark:text-gray-300 select-text space-y-1">
                  {points.map((pt, i) => (
                    <li key={i}>{pt}</li>
                  ))}
                </ul>
              </article>
            ))}
          </div>
        </section>

        {/* Education */}
        <section className="mb-20">
          <h3 className="text-3xl font-extrabold mb-10 text-purple-700 dark:text-purple-400 select-text">
            🎓 Education
          </h3>
          <div className="space-y-6 max-w-3xl mx-auto">
            {education.map(({ degree, institute, period, cgpa, percentage }, idx) => (
              <article
                key={idx}
                className="bg-white dark:bg-gray-800 rounded-3xl p-6 shadow-md"
              >
                <h4 className="text-lg font-bold text-purple-700 dark:text-purple-400 select-text">{degree}</h4>
                <p className="text-sm font-semibold text-gray-600 dark:text-gray-400 select-text">{institute}</p>
                <p className="text-sm text-gray-500 dark:text-gray-400 select-text">
                  {period} {cgpa && `— CGPA: ${cgpa}`} {percentage && `— ${percentage}`}
                </p>
              </article>
            ))}
          </div>
        </section>

        {/* Footer */}
        <motion.footer
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5 }}
          className="bg-white dark:bg-gray-800 rounded-3xl p-8 shadow-lg flex flex-col sm:flex-row items-center justify-between gap-6"
        >
          <div>
            <h4 className="font-bold text-purple-700 dark:text-purple-400 select-text">
              Let's Collaborate! 🤝
            </h4>
            <p className="text-gray-700 dark:text-gray-300 text-lg max-w-md select-text">
              Manual & automation testing | Python automation frameworks | CI/CD pipelines
            </p>
          </div>
          <div className="flex gap-8 text-purple-700 dark:text-purple-400 text-2xl">
            <a
              href="mailto:ajjayya2002@gmail.com"
              className="hover:text-purple-400 transition"
              aria-label="Email"
            >
              <FiMail />
            </a>
            <a
              href="https://github.com/iamajjayya"
              target="_blank"
              rel="noreferrer"
              className="hover:text-purple-400 transition"
              aria-label="GitHub"
            >
              <FiGithub />
            </a>
            <a
              href="https://linkedin.com/in/ajjayya-gv"
              target="_blank"
              rel="noreferrer"
              className="hover:text-purple-400 transition"
              aria-label="LinkedIn"
            >
              <FiLinkedin />
            </a>
          </div>
        </motion.footer>
      </div>
    </div>
  );
}
