import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiArrowUp,
  FiSmile,
  FiCoffee,
} from "react-icons/fi";

const bugQuotes = [
  "üêû Bug? Feature? ü§∑‚Äç‚ôÇÔ∏è Sometimes both!",
  "üîç Testing: Catching bugs before they bite! üêõü¶∑",
  "ü§ñ Keep calm and automate on! ‚ú®",
  "‚úîÔ∏è In QA we trust! Quality First!",
  "üîÑ Manual or automation? Why not both?",
  "üí° Every bug is a hidden opportunity!",
  "üöÄ Test early, test often, ship confidently!",
  "üïµÔ∏è‚Äç‚ôÇÔ∏è Debugging is like being a detective in a crime movie.",
  "üêú If debugging is the process of removing bugs, then programming must be the process of putting them in.",
  "üêû Don't hate the bug, hate the code!",
  "üêõ Found a bug? Call it a feature and ship it.",
  "üéØ A bug a day keeps the users away!",
  "üß™ Quality is not an act, it‚Äôs a habit.",
  "üîß Fix one bug, create two more.",
  "üì¶ Testing is the last defense against chaos.",
  "üßô‚Äç‚ôÇÔ∏è QA is magic, without the wand.",
  "üìä Good testers don‚Äôt find bugs, they find solutions.",
  "üõ†Ô∏è Life‚Äôs too short to test badly.",
  "‚ö° Automation saves time, but creativity saves projects.",
  "üíª Code hard, test harder!",
  "üêù Beehive of bugs? Time to get the smoker.",
  "üî• Hotfixes: QA‚Äôs worst nightmare.",
  "üí£ One bug can blow up your app!",
  "ü¶Ñ Unicorn testers do exist (that's you!).",
  "üí• Crash today, fix tomorrow, learn forever.",
];

const skillsData = {
  "üß™ Testing": [
    "Manual Testing",
    "Functional Testing",
    "Regression Testing",
    "Sanity & Smoke Testing",
    "Cross-Browser Testing",
    "Bug Reporting",
    "SDLC & STLC",
  ],
  "ü§ñ Automation": [
    "Selenium WebDriver",
    "Python",
    "Pytest",
    "Page Object Model (POM)",
    "API Testing",
    "HTML Reports",
    "CI/CD with Jenkins",
    "Agile Methodology",
  ],
  "üíª Technical": [
    "SQL",
    "HTML/CSS/JavaScript",
    "REST API",
    "React.js",
    "Git/GitHub",
    "Jira",
  ],
};

const projects = [
  {
    title: "Selenium Hybrid Automation Framework",
    desc:
      "Hybrid automation framework combining POM and Data-Driven Testing with HTML reporting and Jenkins CI/CD.",
    repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
    tags: ["Python", "Selenium", "Pytest", "POM", "CI/CD"],
  },
  {
    title: "API Automation Framework",
    desc:
      "REST API testing framework using pytest, requests, schema validation, and detailed HTML reports.",
    repo: "https://github.com/iamajjayya/api-automation",
    tags: ["Python", "Pytest", "Requests", "API"],
  },
  {
    title: "Gym Application",
    desc:
      "Workout guide app using ExerciseDB and YouTube APIs built with React.js.",
    repo: "https://github.com/iamajjayya/gym-application",
    live: "https://gymapp.live",
    tags: ["React.js", "REST API", "YouTube API"],
  },
  {
    title: "E-commerce Website",
    desc:
      "Full e-commerce platform with authentication, product listings, cart and checkout built using React.js.",
    repo: "https://github.com/iamajjayya/ecommerce-site",
    live: "https://ecommerceajjayya.netlify.app/",
    tags: ["React.js", "E-commerce", "Authentication", "Shopping Cart"],
  },
];

const experience = [
  {
    role: "QA/QC Engineer",
    company: "RTBDemand.com",
    period: "Sep 2024 ‚Äì Present (Remote)",
    points: [
      "Manual testing for ad-tech systems on desktop & mobile.",
      "Verified ad script integration, refresh functionality, & placement.",
      "Executed sanity, smoke, & regression testing across environments.",
      "Logged defects & collaborated with developers for fixes.",
      "Conducted basic API validations for ad delivery checks.",
    ],
  },
  {
    role: "Front-End Developer Intern",
    company: "Internplus+",
    period: "Jul 2024 ‚Äì Sep 2024 (Remote)",
    points: [
      "Built responsive UI components using React.js.",
      "Integrated and tested APIs in Agile environment.",
    ],
  },
];

// Single Card Component with subtle funny icon on hover
function Card({ title, children, tags }) {
  return (
    <motion.div
      className="bg-white rounded-xl shadow-lg p-6 cursor-pointer relative overflow-hidden"
      whileHover={{ scale: 1.05, boxShadow: "0 0 15px #6366F1" }}
      transition={{ type: "spring", stiffness: 120 }}
    >
      <h3 className="text-indigo-700 font-bold text-xl flex items-center gap-2 select-none">
        {title}
        <motion.span
          className="ml-auto text-indigo-400"
          initial={{ opacity: 0, scale: 0 }}
          whileHover={{ opacity: 1, scale: 1.3, rotate: 10 }}
          transition={{ type: "spring", stiffness: 150 }}
          title="Testing is fun! üòÑ"
        >
          <FiSmile size={20} />
        </motion.span>
      </h3>
      <div className="mt-3 text-gray-700">{children}</div>
      {tags && (
        <div className="mt-4 flex flex-wrap gap-2">
          {tags.map((tag) => (
            <span
              key={tag}
              className="bg-indigo-100 text-indigo-700 px-3 py-1 rounded-full text-xs font-semibold"
            >
              {tag}
            </span>
          ))}
        </div>
      )}
    </motion.div>
  );
}

export default function App() {
  // Ladybugs floating with individual rotation and quote index
  const [ladybugs, setLadybugs] = useState([
    { id: 1, top: "10vh", left: "15vw", quoteIndex: 0, rotating: false },
    { id: 2, top: "60vh", left: "30vw", quoteIndex: 5, rotating: false },
    { id: 3, top: "40vh", left: "75vw", quoteIndex: 12, rotating: false },
  ]);

  // Rotate individual bug on click or hover for 2 sec
  const rotateLadybug = (id) => {
    setLadybugs((prev) =>
      prev.map((bug) =>
        bug.id === id ? { ...bug, rotating: true } : bug
      )
    );
    setTimeout(() => {
      setLadybugs((prev) =>
        prev.map((bug) =>
          bug.id === id ? { ...bug, rotating: false } : bug
        )
      );
    }, 2000);
  };

  // Cycle quotes every 20 sec independently for each bug
  useEffect(() => {
    const intervals = ladybugs.map((bug) =>
      setInterval(() => {
        setLadybugs((prev) =>
          prev.map((b) =>
            b.id === bug.id
              ? {
                  ...b,
                  quoteIndex: (b.quoteIndex + 1) % bugQuotes.length,
                }
              : b
          )
        );
      }, 20000 + bug.id * 3000)
    );
    return () => intervals.forEach(clearInterval);
  }, []);

  // Scroll to top button
  const [showTop, setShowTop] = useState(false);
  useEffect(() => {
    const onScroll = () => setShowTop(window.scrollY > 200);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-indigo-100 font-sans text-gray-900 relative">
      {/* Fixed Header */}
      <motion.header
        initial={{ y: -80 }}
        animate={{ y: 0 }}
        transition={{ type: "spring", stiffness: 120, damping: 25 }}
        className="fixed top-0 left-0 w-full bg-white/95 backdrop-blur-md shadow-md z-50 px-6 py-3 flex flex-col sm:flex-row justify-between items-center gap-3"
      >
        <h1 className="text-3xl font-extrabold text-indigo-700 select-none cursor-default flex items-center gap-2">
          üßë‚Äçüíª Ajjayya G V ‚Äî QA Tester
        </h1>

        <div className="flex gap-6 items-center text-indigo-600">
          <a
            href="mailto:ajjayya2002@gmail.com"
            className="flex items-center gap-1 hover:text-indigo-900 transition"
            aria-label="Email"
          >
            <FiMail /> ajjayya2002@gmail.com
          </a>
          <a
            href="https://linkedin.com/in/ajjayya-gv"
            target="_blank"
            rel="noreferrer"
            className="flex items-center gap-1 hover:text-indigo-900 transition"
            aria-label="LinkedIn"
          >
            <FiLinkedin /> LinkedIn
          </a>
          <a
            href="https://github.com/iamajjayya"
            target="_blank"
            rel="noreferrer"
            className="flex items-center gap-1 hover:text-indigo-900 transition"
            aria-label="GitHub"
          >
            <FiGithub /> GitHub
          </a>
          <motion.a
            whileHover={{ scale: 1.1, backgroundColor: "#4338ca" }}
            href="/resume.pdf"
            download
            className="inline-flex items-center gap-2 px-4 py-2 rounded-md bg-indigo-600 text-white font-semibold shadow-lg transition-colors"
          >
            <FiDownload size={20} /> Resume
          </motion.a>
        </div>
      </motion.header>

      <main className="pt-28 max-w-6xl mx-auto px-6 space-y-20 select-text">
        {/* Profile Summary */}
        <Card title="üë§ Profile Summary">
          <p className="leading-relaxed text-gray-800 text-lg max-w-3xl">
            Detail-oriented QA Tester with <b>1 year</b> of manual testing
            experience across web and ad-tech domains. Skilled in functional,
            regression, sanity, and smoke testing, with proven expertise in
            validating complex ad integrations and collaborating with dev teams
            to resolve issues promptly.
            <br />
            <br />
            Hands-on knowledge in <b>Python automation</b> using Selenium,
            Pytest, API testing, and frameworks based on Page Object Model and
            Data-Driven Testing. Always learning and aiming to grow further in
            automation testing.
          </p>
        </Card>

        {/* Skills */}
        <section>
          <h2 className="text-3xl font-bold mb-8 flex items-center gap-3 select-none">
            üõ†Ô∏è Skills
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            {Object.entries(skillsData).map(([category, skills]) => (
              <motion.div
                key={category}
                className="bg-white rounded-xl shadow-lg p-6 cursor-default"
                whileHover={{ scale: 1.03, boxShadow: "0 0 20px #6366F1" }}
                transition={{ type: "spring", stiffness: 90 }}
              >
                <h3 className="font-extrabold text-indigo-700 mb-4 flex items-center gap-2 select-none">
                  {category}
                </h3>
                <ul className="space-y-3 text-lg">
                  {skills.map((skill) => (
                    <motion.li
                      key={skill}
                      className="hover:text-indigo-600 cursor-default transition-colors"
                      whileHover={{ scale: 1.1, color: "#4F46E5" }}
                    >
                      {skill}
                    </motion.li>
                  ))}
                </ul>
              </motion.div>
            ))}
          </div>
        </section>

        {/* Projects */}
        <section>
          <h2 className="text-3xl font-bold mb-8 flex items-center gap-3 select-none">
            üöÄ Projects
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
            {projects.map(({ title, desc, repo, tags, live }) => (
              <motion.article
                key={title}
                className="border border-indigo-300 rounded-lg p-6 shadow-md cursor-pointer relative overflow-hidden"
                whileHover={{
                  scale: 1.04,
                  boxShadow: "0 0 20px #6366F1",
                  backgroundColor: "rgba(99,102,241,0.05)",
                }}
                transition={{ type: "spring", stiffness: 90 }}
              >
                <h3 className="text-indigo-700 font-bold text-2xl mb-3 flex items-center gap-2 select-none">
                  {title}
                  <motion.span
                    className="ml-auto text-indigo-400"
                    initial={{ opacity: 0, scale: 0 }}
                    whileHover={{ opacity: 1, scale: 1.3, rotate: 10 }}
                    transition={{ type: "spring", stiffness: 150 }}
                    title="Testing is fun! üòÑ"
                  >
                    <FiSmile size={20} />
                  </motion.span>
                </h3>
                <p className="text-gray-700 mb-4 text-lg">{desc}</p>
                <div className="flex flex-wrap gap-3 mb-5">
                  {tags.map((tag) => (
                    <span
                      key={tag}
                      className="bg-indigo-100 text-indigo-700 text-xs font-semibold rounded-full px-3 py-1"
                    >
                      {tag}
                    </span>
                  ))}
                </div>
                <div className="flex gap-6 items-center">
                  <a
                    href={repo}
                    target="_blank"
                    rel="noreferrer"
                    className="text-indigo-600 hover:underline flex items-center gap-1"
                  >
                    GitHub Repo <FiGithub />
                  </a>
                  {live && (
                    <a
                      href={live}
                      target="_blank"
                      rel="noreferrer"
                      className="text-indigo-600 hover:underline flex items-center gap-1"
                    >
                      Live Demo
                    </a>
                  )}
                </div>
              </motion.article>
            ))}
          </div>
        </section>

        {/* Experience */}
        <section>
          <h2 className="text-3xl font-bold mb-8 flex items-center gap-3 select-none">
            üíº Experience
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
            {experience.map(({ role, company, period, points }) => (
              <motion.article
                key={role}
                className="bg-white rounded-xl shadow-lg p-6 cursor-default"
                whileHover={{ scale: 1.03, boxShadow: "0 0 20px #6366F1" }}
                transition={{ type: "spring", stiffness: 90 }}
              >
                <h3 className="text-indigo-700 font-bold text-2xl mb-2 select-none flex items-center gap-2">
                  {role}
                  <motion.span
                    className="ml-auto text-indigo-400"
                    initial={{ opacity: 0, scale: 0 }}
                    whileHover={{ opacity: 1, scale: 1.3, rotate: 10 }}
                    transition={{ type: "spring", stiffness: 150 }}
                    title="Hard work pays off! ‚òï"
                  >
                    <FiCoffee size={20} />
                  </motion.span>
                </h3>
                <p className="text-gray-600 font-semibold mb-3">{company}</p>
                <p className="text-gray-500 mb-4 italic">{period}</p>
                <ul className="list-disc list-inside space-y-2 text-gray-700">
                  {points.map((p) => (
                    <li key={p}>{p}</li>
                  ))}
                </ul>
              </motion.article>
            ))}
          </div>
        </section>
      </main>

      {/* Floating Ladybugs */}
      {ladybugs.map(({ id, top, left, quoteIndex, rotating }) => (
        <motion.div
          key={id}
          animate={{
            top,
            left,
            y: [0, -6, 0, 6, 0],
            x: [0, 3, 0, -3, 0],
            rotate: rotating ? [0, 360] : 0,
          }}
          transition={{
            duration: rotating ? 1.5 : 10 + id * 7,
            repeat: rotating ? 0 : Infinity,
            ease: "easeInOut",
          }}
          className="fixed select-none z-50 cursor-pointer flex flex-col items-center"
          style={{ top, left, position: "fixed", touchAction: "manipulation" }}
          onClick={() => rotateLadybug(id)}
          onMouseEnter={() => rotateLadybug(id)}
          whileHover={{ scale: 1.6, rotate: 15, color: "#6366F1" }}
          whileTap={{ scale: 1.7, rotate: 25, color: "#4F46E5" }}
          title="Click or hover for a spin!"
        >
          <span className="text-5xl select-none">üêû</span>
          <motion.div
            initial={{ opacity: 0.85, y: 5 }}
            animate={{ opacity: 1 }}
            className="max-w-xs bg-indigo-600 text-white text-sm rounded-md px-4 py-1 mt-2 shadow-lg select-text whitespace-nowrap"
          >
            {bugQuotes[quoteIndex]}
          </motion.div>
        </motion.div>
      ))}

      {/* Scroll to Top Button */}
      <AnimatePresence>
        {showTop && (
          <motion.button
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 100 }}
            transition={{ duration: 0.3 }}
            onClick={() => window.scrollTo({ top: 0, behavior: "smooth" })}
            className="fixed bottom-6 right-6 bg-indigo-600 hover:bg-indigo-700 text-white p-3 rounded-full shadow-lg z-50 focus:outline-none"
            aria-label="Scroll to top"
          >
            <FiArrowUp size={26} />
          </motion.button>
        )}
      </AnimatePresence>
    </div>
  );
}
