import React, { useState, useEffect } from "react";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiArrowUp,
} from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";

const bugQuotes = [
  "üêû ‚ÄòIt‚Äôs not a bug, it‚Äôs a feature!‚Äô",
  "ü™≤ ‚ÄòDebugging: The art of removing bugs.‚Äô",
  "üêõ ‚ÄòKeep calm and test on.‚Äô",
  "ü¶ü ‚ÄòCode never lies, tests sometimes do.‚Äô",
  "üêú ‚ÄòA bug in time saves nine!‚Äô",
  "üï∑Ô∏è ‚ÄòBreaking things gently since forever.‚Äô",
  "üêû ‚ÄòTesters turn caffeine into bug reports.‚Äô",
  "ü¶ó ‚ÄòSquashing bugs, one line at a time.‚Äô",
  "üêù ‚ÄòGot bugs? We got logs.‚Äô",
  "ü™∞ ‚ÄòAutomate all the things!‚Äô",
  "ü¶† ‚ÄòTesting is my cardio.‚Äô",
  "üêû ‚ÄòNo bugs harmed during testing.‚Äô",
  "üï∏Ô∏è ‚ÄòCatch me if you can!‚Äô",
  "ü¶ü ‚ÄòFinding bugs faster than light.‚Äô",
  "üêõ ‚ÄòTo err is human; to debug, divine.‚Äô",
  "üêú ‚ÄòEvery bug has a story.‚Äô",
  "üï∑Ô∏è ‚ÄòKeep calm and pass the tests.‚Äô",
  "üêù ‚ÄòMay your tests always pass.‚Äô",
  "ü™∞ ‚ÄòOne bug to rule them all.‚Äô",
  "ü¶ó ‚ÄòHappy testing!‚Äô",
  "üêû ‚ÄòEat, Sleep, Test, Repeat.‚Äô",
  "ü¶† ‚ÄòThe art of breaking things gently.‚Äô",
  "ü¶ü ‚ÄòQA: Quality Awesomeness!‚Äô",
  "üï∏Ô∏è ‚ÄòBetter safe than sorry, test everything!‚Äô",
];

const getRandomPosition = () => {
  const vw = window.innerWidth;
  const vh = window.innerHeight;
  return {
    left: Math.random() * (vw * 0.8) + vw * 0.1,
    top: Math.random() * (vh * 0.8) + vh * 0.1,
  };
};

const bugsCount = 4;

export default function Portfolio() {
  const [quoteIndexes, setQuoteIndexes] = React.useState(
    Array(bugsCount)
      .fill(0)
      .map(() => Math.floor(Math.random() * bugQuotes.length))
  );
  const [positions, setPositions] = React.useState(
    Array(bugsCount).fill(0).map(() => getRandomPosition())
  );
  const [showTop, setShowTop] = React.useState(false);
  const [expandedSkill, setExpandedSkill] = React.useState(null);
  const [expandedProject, setExpandedProject] = React.useState(null);
  const [expandedExp, setExpandedExp] = React.useState(null);

  // Rotate quotes independently every 15s + stagger
  useEffect(() => {
    const intervals = quoteIndexes.map((_, i) =>
      setInterval(() => {
        setQuoteIndexes((prev) => {
          const copy = [...prev];
          copy[i] = (copy[i] + 1) % bugQuotes.length;
          return copy;
        });
      }, 15000 + i * 4000)
    );
    return () => intervals.forEach(clearInterval);
  }, []);

  // Slow floating bugs animation
  useEffect(() => {
    let animationFrameId;
    const speed = 0.15;
    const direction = Array(bugsCount)
      .fill(0)
      .map(() => ({
        x: Math.random() < 0.5 ? -speed : speed,
        y: Math.random() < 0.5 ? -speed : speed,
      }));

    const animate = () => {
      setPositions((prev) =>
        prev.map((pos, i) => {
          let newX = pos.left + direction[i].x;
          let newY = pos.top + direction[i].y;

          const vw = window.innerWidth;
          const vh = window.innerHeight;

          if (newX < 50 || newX > vw - 50) direction[i].x = -direction[i].x;
          if (newY < 50 || newY > vh - 50) direction[i].y = -direction[i].y;

          newX = Math.min(Math.max(newX, 50), vw - 50);
          newY = Math.min(Math.max(newY, 50), vh - 50);

          return { left: newX, top: newY };
        })
      );
      animationFrameId = requestAnimationFrame(animate);
    };
    animationFrameId = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrameId);
  }, []);

  useEffect(() => {
    const onScroll = () => setShowTop(window.scrollY > 300);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: "smooth" });

  const skills = [
    "Manual Testing üß™",
    "Functional Testing ‚öôÔ∏è",
    "Regression Testing üîÑ",
    "Sanity & Smoke Testing üî•",
    "Cross-Browser Testing üåê",
    "Bug Reporting üêû",
    "SDLC üìà",
    "STLC üß©",
    "Agile üèÉ‚Äç‚ôÇÔ∏è",
    "Selenium WebDriver ü§ñ",
    "Python üêç",
    "Pytest ‚úîÔ∏è",
    "Page Object Model (POM) üóÇ",
    "API Testing with Requests üåê",
    "HTML Reports üìä",
    "SQL üóÉÔ∏è",
    "HTML & CSS üé®",
    "JavaScript üíª",
    "React.js ‚öõÔ∏è",
    "REST API üîó",
    "Postman üöÄ",
    "Git & GitHub üîß",
    "Jira üìù",
    "Jenkins üîÑ",
  ];

  const projects = [
    {
      title: "Selenium Hybrid Automation Framework",
      desc: "Hybrid framework using POM and Data-Driven approach with HTML reporting and Jenkins CI/CD integration.",
      repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
      tags: ["Python", "Selenium", "Pytest", "POM", "CI/CD"],
    },
    {
      title: "API Automation Framework",
      desc: "Automated REST API testing with validation, status code checks, and response verification.",
      repo: "https://github.com/iamajjayya/api-automation",
      tags: ["Python", "Pytest", "Requests", "API"],
    },
    {
      title: "Gym Application",
      desc: "Workout guide using ExerciseDB API & YouTube API.",
      repo: "https://github.com/iamajjayya/gym-app",
      live: "https://gym-app-ajjayya.netlify.app/",
      tags: ["React.js", "REST API", "YouTube API"],
    },
    {
      title: "E-commerce Website",
      desc: "React-based e-commerce platform with user authentication, product listings, cart & checkout.",
      repo: "https://github.com/iamajjayya/ecommerce-site",
      live: "https://ecommerceajjayya.netlify.app/",
      tags: ["React.js", "Auth", "Shopping Cart"],
    },
  ];

  const experience = [
    {
      role: "QA Tester",
      company: "RTBDemand.com",
      period: "Sep 2024 ‚Äì Present",
      location: "Remote",
      details: [
        "Performed manual testing for ad-tech systems on desktop and mobile.",
        "Verified ad script integration, refresh functionality, and placement.",
        "Executed sanity, smoke, and regression testing across multiple environments.",
        "Logged defects, created test documentation, and collaborated with developers.",
        "Conducted basic API validations for ad delivery checks.",
      ],
    },
    {
      role: "Front-End Developer Intern",
      company: "Internplus+",
      period: "Jul 2024 ‚Äì Sep 2024",
      location: "Remote",
      details: [
        "Built responsive UI components using React.js.",
        "Integrated and tested APIs in an Agile environment.",
      ],
    },
  ];

  // Toggle expand helpers
  const toggleSkill = (idx) =>
    setExpandedSkill(expandedSkill === idx ? null : idx);
  const toggleProject = (idx) =>
    setExpandedProject(expandedProject === idx ? null : idx);
  const toggleExp = (idx) => setExpandedExp(expandedExp === idx ? null : idx);

  return (
    <div className="min-h-screen bg-gradient-to-br from-white to-blue-100 text-blue-900 font-sans relative">
      {/* Header */}
      <header className="sticky top-0 bg-gradient-to-r from-orange-400 via-orange-300 to-orange-400 shadow-md z-50">
        <div className="max-w-7xl mx-auto flex flex-wrap items-center justify-between px-6 py-3 gap-4">
          <h1 className="font-bold text-2xl cursor-default select-none hover:text-blue-900 transition duration-300">
            Ajjayya G V ‚Äî QA & Automation Tester
          </h1>
          <nav className="flex flex-wrap gap-6 text-blue-900 font-medium text-sm">
            <a
              href="mailto:ajjayya2002@gmail.com"
              className="hover:text-white flex items-center gap-1 transition duration-300"
            >
              <FiMail /> ajjayya2002@gmail.com
            </a>
            <a
              href="https://linkedin.com/in/ajjayya-gv"
              target="_blank"
              rel="noreferrer"
              className="hover:text-white flex items-center gap-1 transition duration-300"
            >
              <FiLinkedin /> LinkedIn
            </a>
            <a
              href="https://github.com/iamajjayya"
              target="_blank"
              rel="noreferrer"
              className="hover:text-white flex items-center gap-1 transition duration-300"
            >
              <FiGithub /> GitHub
            </a>
            <a
              href="/resume.pdf"
              download
              className="bg-blue-600 hover:bg-blue-700 transition rounded-md px-3 py-1 flex items-center gap-2 shadow text-white"
            >
              <FiDownload /> Resume
            </a>
          </nav>
        </div>
      </header>

      {/* Spacer for header */}
      <div className="h-16" />

      {/* Content */}
      <main className="max-w-5xl mx-auto px-6 space-y-12 pb-20">
        {/* Profile */}
        <section>
          <h2 className="text-3xl font-semibold mb-4 select-none flex items-center gap-3">
            üë§ Profile Summary
          </h2>
          <p className="text-blue-900 max-w-3xl leading-relaxed font-medium">
            Detail-oriented QA Tester with 1+ year of manual testing experience on
            web and ad-tech platforms. Skilled in functional, regression, sanity,
            smoke, and cross-browser testing. Strong collaboration with developers
            to resolve issues. Passionate about Python automation with hands-on
            experience in Selenium WebDriver, Pytest, API testing, and framework
            design. Committed to delivering high-quality, reliable software.
          </p>
        </section>

        {/* Skills */}
        <section>
          <h2 className="text-3xl font-semibold mb-6 select-none flex items-center gap-3">
            ‚öôÔ∏è Skills
          </h2>
          <ul className="list-disc list-inside space-y-2 max-w-3xl">
            {skills.map((skill, idx) => (
              <li
                key={idx}
                className="cursor-pointer transition-colors duration-300 hover:text-orange-500"
                onClick={() => toggleSkill(idx)}
                aria-expanded={expandedSkill === idx}
                aria-controls={`skill-desc-${idx}`}
              >
                {skill}
                <AnimatePresence>
                  {expandedSkill === idx && (
                    <motion.p
                      id={`skill-desc-${idx}`}
                      initial={{ opacity: 0, height: 0 }}
                      animate={{ opacity: 1, height: "auto" }}
                      exit={{ opacity: 0, height: 0 }}
                      transition={{ duration: 0.3 }}
                      className="text-sm text-gray-700 dark:text-gray-300 ml-6 mt-1"
                    >
                      {/* Example description - you can customize */}
                      {skill.includes("Manual")
                        ? "Expert in manual testing processes, defect reporting, and test case execution."
                        : skill.includes("Selenium")
                        ? "Hands-on experience automating browsers using Selenium WebDriver."
                        : skill.includes("Python")
                        ? "Proficient in Python scripting for automation frameworks."
                        : skill.includes("Agile")
                        ? "Experience working in Agile/Scrum environments."
                        : "Solid knowledge in this skill area."}
                    </motion.p>
                  )}
                </AnimatePresence>
              </li>
            ))}
          </ul>
        </section>

        {/* Projects */}
        <section>
          <h2 className="text-3xl font-semibold mb-6 select-none flex items-center gap-3">
            üìÅ Projects
          </h2>
          <ul className="space-y-6 max-w-3xl">
            {projects.map(({ title, desc, repo, live, tags }, idx) => (
              <li
                key={idx}
                className="cursor-pointer border border-blue-300 rounded-md p-4 hover:bg-blue-100 transition"
                onClick={() => toggleProject(idx)}
                aria-expanded={expandedProject === idx}
                aria-controls={`project-desc-${idx}`}
              >
                <div className="flex justify-between items-center font-semibold text-blue-900">
                  <span>{title}</span>
                  <div className="flex gap-2 text-orange-500 font-mono text-xs">
                    {tags.map((tag) => (
                      <span key={tag} className="uppercase">
                        {tag}
                      </span>
                    ))}
                  </div>
                </div>
                <AnimatePresence>
                  {expandedProject === idx && (
                    <motion.div
                      id={`project-desc-${idx}`}
                      initial={{ opacity: 0, height: 0 }}
                      animate={{ opacity: 1, height: "auto" }}
                      exit={{ opacity: 0, height: 0 }}
                      transition={{ duration: 0.3 }}
                      className="mt-2 text-sm text-gray-800"
                    >
                      <p className="mb-2">{desc}</p>
                      <a
                        href={repo}
                        target="_blank"
                        rel="noreferrer"
                        className="text-blue-700 underline hover:text-orange-600"
                      >
                        GitHub Repo
                      </a>
                      {live && (
                        <>
                          {" | "}
                          <a
                            href={live}
                            target="_blank"
                            rel="noreferrer"
                            className="text-blue-700 underline hover:text-orange-600"
                          >
                            Live Demo
                          </a>
                        </>
                      )}
                    </motion.div>
                  )}
                </AnimatePresence>
              </li>
            ))}
          </ul>
        </section>

        {/* Experience */}
        <section>
          <h2 className="text-3xl font-semibold mb-6 select-none flex items-center gap-3">
            üíº Experience
          </h2>
          <ul className="space-y-6 max-w-3xl">
            {experience.map(({ role, company, period, location, details }, idx) => (
              <li
                key={idx}
                className="cursor-pointer border border-blue-300 rounded-md p-4 hover:bg-blue-100 transition"
                onClick={() => toggleExp(idx)}
                aria-expanded={expandedExp === idx}
                aria-controls={`exp-desc-${idx}`}
              >
                <div className="flex justify-between items-center font-semibold text-blue-900">
                  <span>
                    {role} @ {company}
                  </span>
                  <span className="text-orange-500 text-sm font-mono">{period}</span>
                </div>
                <AnimatePresence>
                  {expandedExp === idx && (
                    <motion.div
                      id={`exp-desc-${idx}`}
                      initial={{ opacity: 0, height: 0 }}
                      animate={{ opacity: 1, height: "auto" }}
                      exit={{ opacity: 0, height: 0 }}
                      transition={{ duration: 0.3 }}
                      className="mt-2 text-sm text-gray-800"
                    >
                      <p className="italic text-xs mb-2">{location}</p>
                      <ul className="list-disc list-inside space-y-1">
                        {details.map((d, i) => (
                          <li key={i}>{d}</li>
                        ))}
                      </ul>
                    </motion.div>
                  )}
                </AnimatePresence>
              </li>
            ))}
          </ul>
        </section>
      </main>

      {/* Floating Bugs */}
      {positions.map((pos, i) => (
        <motion.div
          key={i}
          className="fixed cursor-pointer select-none z-40 text-3xl"
          style={{ left: pos.left, top: pos.top }}
          initial={{ rotate: 0 }}
          whileHover={{ scale: 1.4, rotate: 360, color: "#F97316" }} // orange on hover
          transition={{ type: "spring", stiffness: 300, damping: 20 }}
          onClick={() =>
            setQuoteIndexes((prev) => {
              const copy = [...prev];
              copy[i] = (copy[i] + 1) % bugQuotes.length;
              return copy;
            })
          }
          aria-label="Funny QA bug"
          title={bugQuotes[quoteIndexes[i]]}
        >
          üêû
          <AnimatePresence>
            <motion.div
              key={quoteIndexes[i]}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 0.95, y: 0 }}
              exit={{ opacity: 0, y: 10 }}
              transition={{ duration: 0.3 }}
              className="absolute -top-10 left-1/2 transform -translate-x-1/2 px-4 py-1 rounded-md bg-orange-400/90 text-white text-xs whitespace-nowrap select-text pointer-events-none"
            >
              {bugQuotes[quoteIndexes[i]]}
            </motion.div>
          </AnimatePresence>
        </motion.div>
      ))}

      {/* Scroll to top button */}
      <AnimatePresence>
        {showTop && (
          <motion.button
            onClick={scrollToTop}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            className="fixed bottom-6 right-6 z-50 rounded-full bg-orange-500 hover:bg-orange-600 shadow-lg p-3 text-white cursor-pointer"
            aria-label="Scroll to top"
            title="Scroll to Top"
          >
            <FiArrowUp size={24} />
          </motion.button>
        )}
      </AnimatePresence>

      {/* Footer */}
      <footer className="bg-gradient-to-r from-orange-400 via-orange-300 to-orange-400 text-white py-4 text-center text-sm select-none">
        ¬© 2025 Ajjayya G V ‚Ä¢ QA & Automation Tester ‚Ä¢ Crafted with ‚ù§Ô∏è & üêû Bugs
      </footer>
    </div>
  );
}
