import React, { useState, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiArrowUp,
} from "react-icons/fi";

const bugQuotes = [
  "Bug? Feature? ü§∑‚Äç‚ôÇÔ∏è Sometimes both! üêû",
  "Testing: Catching bugs before they bite! üêõü¶∑",
  "Keep calm and automate on! ü§ñ‚ú®",
  "In QA we trust! ‚úîÔ∏è Quality First!",
  "Manual or automation? Why not both? üîÑ",
  "Every bug is a hidden opportunity! üîé",
  "Test early, test often, ship confidently! üöÄ",
  "Debugging is like being a detective in a crime movie. üïµÔ∏è‚Äç‚ôÇÔ∏è",
];

const skillsData = {
  Testing: [
    "Manual Testing üß™ - Functional, Regression, Smoke, Sanity",
    "Bug Reporting üêõ - Clear, Reproducible, Priority setting",
    "SDLC & STLC üìà - Deep understanding of software lifecycle",
    "Cross-Browser Testing üåê - Chrome, Firefox, Safari, Edge",
    "Agile Methodology üèÉ‚Äç‚ôÇÔ∏è - Sprint planning, Scrum ceremonies",
  ],
  Automation: [
    "Selenium WebDriver üñ•Ô∏è - Browser automation expertise",
    "Python üêç - Strong scripting & automation skills",
    "Pytest ‚úÖ - Modular and scalable test frameworks",
    "Page Object Model üì¶ - Clean & maintainable test design",
    "API Testing üîå - Requests, validation, JSON schema",
    "HTML Reports üìä - Clear visual test results",
  ],
  Technical: [
    "SQL üóÑÔ∏è - Complex queries, database validations",
    "HTML/CSS/JavaScript üé® - Frontend knowledge for UI testing",
    "REST API üåç - Endpoints, status codes, payloads",
    "React.js ‚öõÔ∏è - Experience in building UIs",
    "Git & GitHub üêô - Version control and collaboration",
    "Jira & Jenkins ‚öôÔ∏è - Bug tracking & CI/CD pipelines",
  ],
};

const projects = [
  {
    title: "Selenium Hybrid Automation Framework",
    desc:
      "Developed a robust hybrid automation framework combining Page Object Model and Data-Driven Testing. Integrated HTML reports and seamless Jenkins CI/CD pipeline for automated regression runs.",
    repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
    tags: ["Python", "Selenium", "Pytest", "POM", "CI/CD"],
  },
  {
    title: "API Automation Framework",
    desc:
      "Built a comprehensive API testing framework leveraging pytest and Requests library, implementing schema validation and robust endpoint coverage with detailed HTML reports.",
    repo: "https://github.com/iamajjayya/api-automation",
