import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
  Radar, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, ResponsiveContainer,
} from "recharts";

// ======================== Helpers ========================
const cx = (...classes) => classes.filter(Boolean).join(" ");
const save = (k, v) => localStorage.setItem(k, JSON.stringify(v));
const load = (k, d) => {
  try { const v = localStorage.getItem(k); return v ? JSON.parse(v) : d; } catch { return d; }
};

// ======================== Dark Mode Toggle ========================
function ThemeToggle({ dark, setDark }) {
  useEffect(() => { save("theme-dark", dark); }, [dark]);
  return (
    <motion.button
      onClick={() => setDark(!dark)}
      className="fixed bottom-8 left-8 z-50 rounded-full px-4 py-2 text-sm font-semibold shadow-lg bg-white/70 backdrop-blur-md dark:bg-zinc-900/70"
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      aria-label="Toggle theme"
    >
      {dark ? "‚òÄÔ∏è Light" : "üåô Dark"}
    </motion.button>
  );
}

// ======================== AI Chatbot ========================
function AIChatbot() {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState([
    { from: "bot", text: "Hi üëã I‚Äôm Ajjayya AI. Ask about skills, projects, resume, or email me!" },
  ]);
  const [input, setInput] = useState("");

  const replyTo = (q) => {
    const t = q.toLowerCase();
    if (t.includes("skill")) return "Core: Selenium ‚Ä¢ Pytest ‚Ä¢ API Testing ‚Ä¢ React ‚Ä¢ SQL ‚Ä¢ Jenkins ‚Ä¢ Git.";
    if (t.includes("project")) return "Top picks: Selenium Hybrid Automation Framework, API Automation, E-commerce, Gym App.";
    if (t.includes("resume") || t.includes("cv")) return "Click ‚ÄòResume‚Äô in header to preview or download the PDF.";
    if (t.includes("contact") || t.includes("email")) return "Use the Contact form below or mail: ajjayya2002@gmail.com";
    if (t.includes("experience")) return "QA/QC @ RTBDemand (Sep 2024‚ÄìNow). FE Intern @ Internplus+ (Jul‚ÄìSep 2024).";
    return "Great question! I‚Äôll add that soon. Meanwhile, try: ‚Äòskills‚Äô, ‚Äòprojects‚Äô, ‚Äòresume‚Äô, ‚Äòexperience‚Äô, ‚Äòcontact‚Äô.";
  };

  const handleSend = (text) => {
    const userText = (text ?? input).trim();
    if (!userText) return;
    const userMsg = { from: "user", text: userText };
    const botMsg = { from: "bot", text: replyTo(userText) };
    setMessages((prev) => [...prev, userMsg, botMsg]);
    setInput("");
  };

  const Quick = ({ label }) => (
    <button
      onClick={() => handleSend(label)}
      className="text-xs bg-indigo-100 dark:bg-indigo-900/40 text-indigo-700 dark:text-indigo-200 px-2 py-1 rounded-full"
    >
      {label}
    </button>
  );

  return (
    <div>
      <AnimatePresence>
        {open && (
          <motion.div
            initial={{ opacity: 0, scale: 0.9, y: 10 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.95, y: 10 }}
            className="fixed bottom-24 right-8 w-80 max-w-[90vw] bg-white/80 dark:bg-zinc-900/80 backdrop-blur-lg border border-zinc-200 dark:border-zinc-800 rounded-2xl shadow-2xl p-4 flex flex-col z-50"
          >
            <div className="flex items-center justify-between mb-2">
              <div className="font-semibold">Ask Ajjayya ü§ñ</div>
              <button className="text-zinc-500 hover:text-zinc-700 dark:hover:text-zinc-300" onClick={() => setOpen(false)}>‚úñ</button>
            </div>

            <div className="flex gap-2 mb-2 flex-wrap">
              <Quick label="Skills" /><Quick label="Projects" /><Quick label="Resume" /><Quick label="Experience" /><Quick label="Contact" />
            </div>

            <div className="flex-1 overflow-y-auto space-y-2 max-h-64 pr-1">
              {messages.map((m, i) => (
                <div key={i} className={m.from === "bot" ? "text-indigo-700 dark:text-indigo-300" : "text-right"}>
                  <span className={cx(
                    "inline-block px-3 py-2 rounded-2xl max-w-[85%] text-sm",
                    m.from === "bot"
                      ? "bg-indigo-50/70 dark:bg-indigo-950/50"
                      : "bg-zinc-100/70 dark:bg-zinc-800/60"
                  )}>
                    {m.text}
                  </span>
                </div>
              ))}
            </div>

            <div className="flex mt-3">
              <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && handleSend()}
                className="flex-1 border dark:border-zinc-700 rounded-l-xl px-3 py-2 text-sm bg-white/70 dark:bg-zinc-900/40"
                placeholder="Ask me anything‚Ä¶"
              />
              <button
                onClick={() => handleSend()}
                className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 rounded-r-xl text-sm"
              >
                Send
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      <motion.button
        onClick={() => setOpen(!open)}
        className="fixed bottom-8 right-8 bg-indigo-600 hover:bg-indigo-700 text-white p-4 rounded-full shadow-xl z-50"
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.95 }}
        aria-label="Open AI Assistant"
      >
        ü§ñ
      </motion.button>
    </div>
  );
}

// ======================== Bug Hunt ========================
function BugHunt({ onWin }) {
  const [score, setScore] = useState(0);
  const handleClick = () => {
    const n = score + 1;
    setScore(n);
    if (n === 3) { onWin?.(); setScore(0); }
  };

  return (
    <motion.div
      onClick={handleClick}
