}

// ======================== Test Demo ========================
function TestDemo({ name = "Project" }) {
  const [logs, setLogs] = useState([]);
  const [running, setRunning] = useState(false);
  const testLogs = [
    (n) => `Running tests on ${n}‚Ä¶`,
    "‚úÖ Login test passed",
    "‚úÖ Add to cart test passed",
    "‚úÖ Checkout flow passed",
    "üöÄ All tests successful!",
  ];

  const run = async () => {
    if (running) return;
    setRunning(true);
    setLogs([]);
    for (let i = 0; i < testLogs.length; i++) {
      await new Promise((r) => setTimeout(r, 850));
      setLogs((prev) => [...prev, typeof testLogs[i] === "function" ? testLogs[i](name) : testLogs[i]]);
    }
    setRunning(false);
  };

  return (
    <div className="mt-4">
      <button
        onClick={run}
        className="bg-indigo-600 hover:bg-indigo-700 text-white px-3 py-1 rounded shadow"
        disabled={running}
      >
        {running ? "Running‚Ä¶" : "‚ñ∂Ô∏è Run Tests"}
      </button>
      <div className="bg-black/90 text-green-400 font-mono text-xs mt-2 p-3 rounded h-32 overflow-y-auto">
        {logs.map((line, i) => (<div key={i}>{line}</div>))}
      </div>
    </div>
  );
}

