// ======================== Contact (EmailJS) ========================
function ContactForm() {
  const [state, setState] = useState({ name: "", email: "", message: "" });
  const [sent, setSent] = useState(false);
  const [sending, setSending] = useState(false);
  const onChange = (e) => setState((s) => ({ ...s, [e.target.name]: e.target.value }));

  const send = async (e) => {
    e.preventDefault();
    setSending(true);
    try {
      // Use @emailjs/browser
      // 1) npm i @emailjs/browser
      // 2) Replace SERVICE_ID, TEMPLATE_ID, PUBLIC_KEY below
      const { default: emailjs } = await import("@emailjs/browser");
      await emailjs.send(
        "SERVICE_ID",
        "TEMPLATE_ID",
        { from_name: state.name, reply_to: state.email, message: state.message },
        { publicKey: "PUBLIC_KEY" }
      );
      setSent(true);
      setState({ name: "", email: "", message: "" });
    } catch (err) {
      alert("Failed to send. Please email me at ajjayya2002@gmail.com");
      // console.error(err);
    } finally {
      setSending(false);
    }
  };

  return (
    <form onSubmit={send} className="space-y-4">
      <div className="grid md:grid-cols-2 gap-3">
        <input
          name="name" value={state.name} onChange={onChange} required
          placeholder="Your name"
          className="px-3 py-2 rounded-xl border border-zinc-300 dark:border-zinc-700 bg-white/70 dark:bg-zinc-900/40"
        />
        <input
          name="email" type="email" value={state.email} onChange={onChange} required
          placeholder="Your email"
          className="px-3 py-2 rounded-xl border border-zinc-300 dark:border-zinc-700 bg-white/70 dark:bg-zinc-900/40"
        />
      </div>
      <textarea
        name="message" value={state.message} onChange={onChange} required rows={4}
        placeholder="Message"
        className="w-full px-3 py-2 rounded-xl border border-zinc-300 dark:border-zinc-700 bg-white/70 dark:bg-zinc-900/40"
      />
      <div className="flex items-center gap-3">
        <button
          disabled={sending}
          className="px-4 py-2 bg-indigo-600 hover:bg-indigo-700 text-white rounded-xl font-semibold"
        >
          {sending ? "Sending‚Ä¶" : "Send"}
        </button>
        {sent && <span className="text-green-600 dark:text-green-400 text-sm">Thanks! I‚Äôll reply soon.</span>}
      </div>
    </form>
  );
}

