// Animate section on scroll hook
function AnimatedSection({ children }) {
  const [ref, inView] = useInView({ triggerOnce: true, threshold: 0.15 });
  return (
    <motion.section
      ref={ref}
      initial={{ opacity: 0, y: 40 }}
      animate={inView ? { opacity: 1, y: 0 } : {}}
      transition={{ duration: 0.7, ease: "easeOut" }}
      className="mb-20"
    >
      {children}
    </motion.section>
  );
}

function FloatingBug({ id }) {
  const [position, setPosition] = useState({
    top: Math.random() * 80 + 10 + "%",
    left: Math.random() * 80 + 10 + "%",
  });
  const [quoteIndex, setQuoteIndex] = useState(id % bugQuotes.length);

  useEffect(() => {
    // Move bug randomly every 7 seconds
    const interval = setInterval(() => {
      setPosition({
        top: Math.random() * 80 + 10 + "%",
        left: Math.random() * 80 + 10 + "%",
      });
      setQuoteIndex((prev) => (prev + 1) % bugQuotes.length);
    }, 7000);

    return () => clearInterval(interval);
  }, []);

  return (
    <motion.div
      aria-label="Funny bug"
      title={bugQuotes[quoteIndex]}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1, rotate: [0, 10, -10, 0] }}
      transition={{ duration: 1, repeat: Infinity, repeatType: "mirror" }}
      style={{
        position: "fixed",
        top: position.top,
        left: position.left,
        zIndex: 1000,
        fontSize: "2rem",
        cursor: "pointer",
        userSelect: "none",
      }}
      whileHover={{
        scale: 1.5,
        rotate: 360,
        transition: { duration: 1 },
      }}
      onClick={() => alert(bugQuotes[quoteIndex])}
    >
      🐞
    </motion.div>
  );
}

