import React, { useEffect, useState } from "react";
import { FiDownload, FiMail, FiGithub, FiLinkedin } from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";

const fadeInUp = {
  initial: { opacity: 0, y: 20 },
  animate: { opacity: 1, y: 0, transition: { duration: 0.6 } },
};

const sectionTitleClass =
  "text-4xl font-extrabold text-indigo-700 mb-8 flex items-center gap-3 select-none";

const contactLinks = [
  { icon: <FiMail />, href: "mailto:ajjayya2002@gmail.com", label: "Email" },
  { icon: <FiGithub />, href: "https://github.com/iamajjayya", label: "GitHub" },
  { icon: <FiLinkedin />, href: "https://linkedin.com/in/ajjayya-gv", label: "LinkedIn" },
];

export default function ModernPortfolio() {
  const [showHeader, setShowHeader] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [scrollUpEffect, setScrollUpEffect] = useState(0);

  // Header show/hide on desktop scroll
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      if (currentScrollY < 50) {
        setShowHeader(true);
      } else if (currentScrollY > lastScrollY) {
        setShowHeader(false);
      } else {
        setShowHeader(true);
      }
      setLastScrollY(currentScrollY);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [lastScrollY]);

  // Mobile scroll up effect: content moves up 20px on scroll down, back on scroll up
  useEffect(() => {
    let lastY = 0;
    const handleMobileScroll = () => {
      if (window.innerWidth <= 768) {
        const currentY = window.scrollY;
        if (currentY > lastY) {
          // scrolling down
          setScrollUpEffect(20);
        } else {
          setScrollUpEffect(0);
        }
        lastY = currentY;
      }
    };
    window.addEventListener("scroll", handleMobileScroll);
    return () => window.removeEventListener("scroll", handleMobileScroll);
  }, []);

  return (
    <>
      {/* Fixed Header */}
      <AnimatePresence>
        {showHeader && (
          <motion.header
            initial={{ y: -100 }}
            animate={{ y: 0, transition: { duration: 0.5, ease: "easeOut" } }}
            exit={{ y: -100, transition: { duration: 0.4, ease: "easeIn" } }}
            className="fixed top-0 left-0 right-0 bg-indigo-700 bg-opacity-95 backdrop-blur-md shadow-lg z-50"
          >
            <div className="max-w-7xl mx-auto flex justify-between items-center py-4 px-8 md:px-20 select-none">
              <div>
                <h1 className="text-white text-3xl font-bold tracking-wide cursor-default">
                  Ajjayya G V
                </h1>
                <p className="text-indigo-300 font-medium mt-0.5 text-sm tracking-wider">
                  QA Automation Tester | Python | Manual & Automation Testing
                </p>
                <div className="mt-1 flex gap-6 text-indigo-300 text-xl">
                  {contactLinks.map(({ icon, href, label }) => (
                    <a
                      key={label}
                      href={href}
                      target="_blank"
                      rel="noopener noreferrer"
                      aria-label={label}
                      className="hover:text-white transition-colors"
                    >
                      {icon}
                    </a>
                  ))}
                </div>
              </div>
              <motion.a
                href="/resume.pdf"
                target="_blank"
                rel="noopener noreferrer"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.95 }}
                className="inline-flex items-center gap-2 bg-indigo-500 hover:bg-indigo-600 text-white font-semibold rounded-lg px-5 py-2 shadow-lg transition-colors"
                aria-label="Download Resume"
              >
                <FiDownload size={20} /> Resume
              </motion.a>
            </div>
          </motion.header>
        )}
      </AnimatePresence>

      {/* Main content with scroll up effect on mobile */}
      <motion.main
        style={{ y: scrollUpEffect }}
        transition={{ type: "spring", stiffness: 120, damping: 14 }}
        className="pt-28 min-h-screen bg-gradient-to-tr from-indigo-50 to-purple-100 text-gray-900 font-sans px-8 md:px-20 pb-20"
      >
        {/* Profile Summary */}
        <motion.section
          className="max-w-4xl mx-auto bg-white rounded-3xl shadow-xl p-12 mb-20 relative overflow-hidden"
          initial="initial"
          whileInView="animate"
          viewport={{ once: true }}
          variants={fadeInUp}
        >
          {/* Soft gradient highlight behind text */}
          <div
            aria-hidden="true"
            className="absolute inset-0 bg-gradient-to-tr from-indigo-100 to-purple-200 opacity-30 rounded-3xl -z-10"
          ></div>
          <h2 className={sectionTitleClass}>
            <span role="img" aria-label="sparkles">
              ‚ú®
            </span>
            Profile Summary
          </h2>
          <p className="text-lg leading-relaxed text-gray-800 max-w-3xl select-text">
            Detail-oriented QA Tester with <strong>1+ year</strong> of professional experience in manual testing of
            web and ad-tech platforms. Skilled in functional, regression, sanity, and smoke testing, validating ad
            integrations, and collaborating with developers for issue resolution. Possess strong theoretical and
            hands-on knowledge of <strong>Python-based Selenium automation</strong> and API testing through personal
            projects, committed to transitioning into full automation roles.
          </p>
        </motion.section>

        {/* Skills */}
        <motion.section
          className="max-w-5xl mx-auto mb-20"
          initial="initial"
          whileInView="animate"
          viewport={{ once: true }}
          variants={fadeInUp}
        >
          <h2 className={sectionTitleClass}>
            <span role="img" aria-label="tools">
              üõ†Ô∏è
            </span>
            Skills
          </h2>

          {/* Skills groups */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-5xl mx-auto">
            <div>
              <h3 className="text-xl font-semibold mb-4 flex items-center gap-2 text-indigo-700 select-none">
                üß™ Testing
              </h3>
              <ul className="list-disc list-inside text-gray-700 space-y-1">
                <li>Manual Testing</li>
                <li>Functional Testing</li>
                <li>Regression Testing</li>
                <li>Sanity & Smoke Testing</li>
                <li>Cross-Browser Testing</li>
                <li>Bug Reporting</li>
              </ul>
            </div>
            <div>
              <h3 className="text-xl font-semibold mb-4 flex items-center gap-2 text-indigo-700 select-none">
                ü§ñ Automation & CI/CD
              </h3>
              <ul className="list-disc list-inside text-gray-700 space-y-1">
                <li>Selenium WebDriver</li>
                <li>Python</li>
                <li>Pytest</li>
                <li>Page Object Model (POM)</li>
                <li>API Testing with Python Requests</li>
                <li>Jenkins & Docker</li>
                <li>Git & GitHub</li>
              </ul>
            </div>
            <div>
              <h3 className="text-xl font-semibold mb-4 flex items-center gap-2 text-indigo-700 select-none">
                üíª Programming & Tools
              </h3>
              <ul className="list-disc list-inside text-gray-700 space-y-1">
                <li>SQL</li>
                <li>HTML, CSS, JavaScript</li>
                <li>React.js</li>
                <li>Postman</li>
                <li>Jira</li>
              </ul>
            </div>
          </div>
        </motion.section>

        {/* Professional Experience */}
        <motion.section
          className="max-w-6xl mx-auto mb-20"
          initial="initial"
          whileInView="animate"
          viewport={{ once: true }}
          variants={fadeInUp}
        >
          <h2 className={sectionTitleClass}>
            <span role="img" aria-label="briefcase">
              üíº
            </span>
            Professional Experience
          </h2>

          {/* Experience Items */}
          {[
            {
              title: "QA/QC Engineer",
              company: "RTBDemand.com",
              date: "Sep 2024 ‚Äì Present (Remote)",
              description:
                "Performed manual testing for ad-tech systems on desktop and mobile. Verified ad script integration, refresh functionality, and placement. Executed sanity, smoke, and regression testing across multiple environments. Logged defects, created test documentation, and collaborated with developers. Conducted basic API validations for ad delivery checks.",
            },
            {
              title: "Front-End Developer Intern",
              company: "Internplus+",
              date: "Jul 2024 ‚Äì Sep 2024 (Remote)",
              description:
                "Built responsive UI components using React.js. Integrated and tested APIs in an Agile environment.",
            },
            {
              title: "Operation Executive & Catalog Data Analyst",
              company: "Zeitgeist Retail Pvt. Ltd.",
              date: "Dec 2023 ‚Äì May 2024 (Internship)",
              description: "Managed catalog data and operational support tasks.",
            },
            {
              title: "Data Analyst (Part-time)",
              company: "Dhi Innovations",
              date: "May 2023 ‚Äì Jul 2023",
              description: "Performed data analysis and reporting tasks.",
            },
          ].map(({ title, company, date, description }) => (
            <motion.article
              key={title}
              whileHover={{ scale: 1.02, boxShadow: "0 10px 25px rgba(67, 56, 202, 0.3)" }}
              className="bg-white rounded-3xl p-8 shadow-lg mb-8 cursor-default transition-shadow duration-300"
            >
              <h3 className="text-2xl font-semibold text-indigo-700 mb-1">{title}</h3>
              <p className="text-indigo-500 italic mb-2 select-text">
                {company} &nbsp;|&nbsp; {date}
              </p>
              <p className="text-gray-700 leading-relaxed">{description}</p>
            </motion.article>
          ))}
        </motion.section>

        {/* Projects */}
        <motion.section
          className="max-w-7xl mx-auto mb-24"
          initial="initial"
          whileInView="animate"
          viewport={{ once: true }}
          variants={fadeInUp}
        >
          <h2 className={sectionTitleClass}>
            <span role="img" aria-label="project">
              üöÄ
            </span>
            Projects
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
            {[
              {
                title: "Selenium Hybrid Automation Framework",
                date: "May 2025 - Jun 2025",
                description:
                  "Hybrid automation framework using Page Object Model and Data-Driven approach with HTML reports and Jenkins CI/CD integration.",
                tech: ["Python", "Selenium", "Pytest", "HTML Reports", "Jenkins", "Docker"],
                repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
              },
              {
                title: "API Automation Framework",
                date: "Aug 2025",
                description:
                  "Automated REST API testing with validation of endpoints, status codes, response content, and schema using pytest and requests.",
                tech: ["Python", "Pytest", "Requests", "Jenkins"],
                repo: "https://github.com/iamajjayya/api-automation",
              },
              {
                title: "E-commerce Website",
                date: "Live",
                description:
                  "React.js e-commerce platform with user authentication, product listings, shopping cart, and checkout flow.",
                tech: ["React.js", "REST API", "Authentication"],
                live: "https://ecommerceajjayya.netlify.app/",
              },
              {
                title: "Gym Application",
                date: "Live",
                description:
                  "Workout guide app using ExerciseDB API & YouTube API for exercise videos.",
                tech: ["React.js", "REST API", "YouTube API"],
                live: "#",
              },
            ].map(({ title, date, description, tech, repo, live }) => (
              <motion.article
                key={title}
                whileHover={{ scale: 1.03, boxShadow: "0 15px 40px rgba(67, 56, 202, 0.4)" }}
                className="bg-white rounded-3xl p-8 shadow-lg flex flex-col justify-between transition-shadow duration-300"
              >
                <div>
                  <h3 className="text-2xl font-semibold text-indigo-700 mb-1 select-text">{title}</h3>
                  <p className="text-indigo-500 italic mb-4 select-text">{date}</p>
                  <p className="text-gray-700 mb-6 leading-relaxed select-text">{description}</p>
                </div>
                <div className="flex flex-wrap gap-2 mb-4">
                  {tech.map((t) => (
                    <motion.span
                      key={t}
                      whileHover={{ scale: 1.2, color: "#4338CA" }}
                      className="inline-block bg-indigo-100 text-indigo-700 px-3 py-1 rounded-full text-sm font-semibold select-none"
                    >
                      {t}
                    </motion.span>
                  ))}
                </div>
                <div className="flex gap-6">
                  {repo && (
                    <a
                      href={repo}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-700 hover:underline font-semibold flex items-center gap-2 select-text"
                    >
                      View Repo <FiGithub />
                    </a>
                  )}
                  {live && (
                    <a
                      href={live}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-700 hover:underline font-semibold select-text"
                    >
                      Live Site
                    </a>
                  )}
                </div>
              </motion.article>
            ))}
          </div>
        </motion.section>

        {/* Footer */}
        <footer className="text-center text-gray-500 text-sm mb-10 select-none">
          &copy; {new Date().getFullYear()} Ajjayya G V ‚Äî QA Automation Tester
        </footer>
      </motion.main>
    </>
  );
}
