import React, { useEffect, useState } from "react";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiArrowUp,
} from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";
import { useInView } from "react-intersection-observer";

// ------------------- DATA -------------------
const contactLinks = [
  { icon: <FiMail />, href: "mailto:ajjayya2002@gmail.com", label: "Email" },
  { icon: <FiGithub />, href: "https://github.com/iamajjayya", label: "GitHub" },
  { icon: <FiLinkedin />, href: "https://linkedin.com/in/ajjayya-gv", label: "LinkedIn" },
];

const skills = {
  Testing: [
    "üìù Manual Testing","‚úîÔ∏è Functional Testing","üîÑ Regression Testing",
    "üî• Sanity & Smoke Testing","üåê Cross-Browser Testing","üêû Bug Reporting",
    "üìä SDLC","üõ†Ô∏è STLC","üóíÔ∏è Test Case Design & Execution",
    "üîç Defect Tracking & Reporting","üß™ Exploratory Testing","üöÄ Agile Methodology",
  ],
  Automation: [
    "üêç Python","üï∑Ô∏è Selenium WebDriver","üß™ Pytest","üìÑ Page Object Model (POM)",
    "üåê API Testing (Requests)","‚öôÔ∏è Jenkins CI/CD","üîß Git & GitHub",
  ],
  Tools: ["üóÑÔ∏è SQL","üíª HTML, CSS, JavaScript","‚öõÔ∏è React.js","üì® Postman","üìã Jira"],
};

const projects = [
  {
    title: "Selenium Hybrid Automation Framework",
    date: "May 2025 - Jun 2025",
    description:
      "Hybrid automation framework using Page Object Model & Data-Driven approach with HTML reports and Jenkins CI/CD integration.",
    tech: ["üêç Python", "üï∑Ô∏è Selenium", "üß™ Pytest", "üìÑ HTML Reports", "üõ†Ô∏è Jenkins"],
    repo: "https://github.com/iamajjayya/Automation_framework.git",
  },
  {
    title: "API Automation Framework",
    date: "Aug 2025",
    description:
      "Automated REST API testing with validation of endpoints, status codes, and response content.",
    tech: ["üêç Python", "üß™ Pytest", "üåê Requests", "üõ†Ô∏è Jenkins"],
    repo: "https://github.com/iamajjayya/api_automation_framework.git",
  },
  {
    title: "üõí E-commerce Website",
    date: "Live",
    description:
      "React.js e-commerce platform with user authentication, product listings, shopping cart, and checkout flow.",
    tech: ["‚öõÔ∏è React.js", "üîê Authentication", "üåê REST API"],
    live: "https://ecommerceajjayya.netlify.app/",
  },
  {
    title: "üí™ Gym Application",
    date: "Live",
    description:
      "Workout guide app using ExerciseDB and YouTube APIs for exercise videos and routines.",
    tech: ["‚öõÔ∏è React.js", "üåê REST API", "‚ñ∂Ô∏è YouTube API"],
    live: "https://gymajjayya.netlify.app/",
  },
];

const experience = [
  {
    role: "QA/QC Engineer",
    company: "RTBDemand.com",
    duration: "Sep 2024 ‚Äì Present (Remote)",
    points: [
      "Performed manual testing for ad-tech systems on desktop and mobile.",
      "Verified ad script integration, refresh functionality, and placement.",
      "Executed sanity, smoke, and regression testing across multiple environments.",
      "Logged defects, created test documentation, collaborated with developers.",
      "Conducted basic API validations for ad delivery checks.",
    ],
  },
  {
    role: "Front-End Developer Intern",
    company: "Internplus+",
    duration: "Jul 2024 ‚Äì Sep 2024 (Remote)",
    points: [
      "Built responsive UI components using React.js.",
      "Integrated and tested APIs in an Agile environment.",
    ],
  },
];

// ------------------- BUG QUOTES -------------------
const bugQuotes = [
  "Keep calm and test on! üêû","Bug found? Celebrate! üéâ","Automate all the things! ü§ñ",
  "A bug a day keeps the user away. üòú","Good testers find bugs, great testers break code! üí•",
  "Test early, test often! üß™","If it ain't tested, it ain't right! ‚úîÔ∏è",
];

// ------------------- HOOKS -------------------
function useRandomQuote(quotes) {
  const [quote, setQuote] = useState(
    quotes[Math.floor(Math.random() * quotes.length)]
  );
  useEffect(() => {
    let timeoutId;
    function scheduleNext() {
      const nextIn = Math.random() * 5000 + 15000;
      timeoutId = setTimeout(() => {
        setQuote(quotes[Math.floor(Math.random() * quotes.length)]);
        scheduleNext();
      }, nextIn);
    }
    scheduleNext();
    return () => clearTimeout(timeoutId);
  }, [quotes]);
  return quote;
}

// ------------------- COMPONENTS -------------------
function Bug({ size = 60, variant = 1 }) {
  const quote = useRandomQuote(bugQuotes);
  const [pos, setPos] = useState({
    x: Math.random() * (window.innerWidth - size),
    y: Math.random() * (window.innerHeight - size),
  });
  useEffect(() => {
    let animationFrameId;
    let direction = { x: 1, y: 1 };
    let speed = 0.2 + Math.random() * 0.2;
    function move() {
      setPos((prev) => {
        let nextX = prev.x + direction.x * speed;
        let nextY = prev.y + direction.y * speed;
        if (nextX < 0 || nextX > window.innerWidth - size) direction.x *= -1;
        if (nextY < 0 || nextY > window.innerHeight - size) direction.y *= -1;
        return {
          x: Math.min(Math.max(nextX, 0), window.innerWidth - size),
          y: Math.min(Math.max(nextY, 0), window.innerHeight - size),
        };
      });
      animationFrameId = requestAnimationFrame(move);
    }
    animationFrameId = requestAnimationFrame(move);
    return () => cancelAnimationFrame(animationFrameId);
  }, [size]);

  const styleBase = {
    position: "fixed",
    top: pos.y,
    left: pos.x,
    width: size,
    height: size,
    fontSize: size * 0.8,
    cursor: "default",
    userSelect: "none",
    zIndex: 9999,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: variant === 1 ? "#7c3aed" : "#facc15",
    filter: "drop-shadow(0 0 10px #7c3aed88)",
    animation: "bugRotate 6s linear infinite",
  };
  return (
    <>
      <style>{`
        @keyframes bugRotate {
          0% { transform: rotate(0deg); }
          50% { transform: rotate(15deg); }
          100% { transform: rotate(0deg); }
        }
      `}</style>
      <div style={styleBase} title={quote} aria-label={quote}>
        üêû
      </div>
    </>
  );
}

function AnimatedSection({ children }) {
  const [ref, inView] = useInView({ triggerOnce: true, threshold: 0.15 });
  return (
    <motion.section
      ref={ref}
      initial={{ opacity: 0, y: 40 }}
      animate={inView ? { opacity: 1, y: 0 } : {}}
      transition={{ duration: 0.7, ease: "easeOut" }}
      className="mb-20"
    >
      {children}
    </motion.section>
  );
}

function ThemeToggle({ theme, toggleTheme }) {
  return (
    <motion.button
      onClick={toggleTheme}
      whileHover={{ scale: 1.2 }}
      whileTap={{ scale: 0.9 }}
      className="relative w-16 h-8 rounded-full 
                 bg-gradient-to-r from-indigo-500 to-purple-700 
                 dark:from-gray-700 dark:to-black
                 shadow-inner flex items-center p-1"
    >
      <motion.div
        animate={{ x: theme === "dark" ? 32 : 0 }}
        transition={{ type: "spring", stiffness: 300 }}
        className="w-6 h-6 rounded-full bg-white dark:bg-yellow-400 shadow-lg"
      />
    </motion.button>
  );
}

// ------------------- MAIN -------------------
export default function Portfolio() {
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [theme, setTheme] = useState(localStorage.getItem("theme") || "light");

  useEffect(() => {
    document.documentElement.classList.toggle("dark", theme === "dark");
    localStorage.setItem("theme", theme);
  }, [theme]);

  useEffect(() => {
    const onScroll = () => setShowScrollTop(window.scrollY > 300);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: "smooth" });
  const toggleTheme = () => setTheme(theme === "light" ? "dark" : "light");

  return (
    <>
      {/* Background Web3 grid */}
      <div className="fixed inset-0 -z-10">
        <div className="absolute inset-0 bg-gradient-to-br from-indigo-900 via-purple-900 to-black opacity-90"></div>
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,_#7c3aed33_1px,_transparent_1px)] bg-[length:40px_40px]" />
      </div>

      {/* Header */}
      <header className="fixed top-0 left-0 right-0 bg-white/30 dark:bg-black/30 backdrop-blur-md shadow-lg z-50">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between px-6 py-4 gap-4 md:gap-0">
          <motion.h1
            className="text-indigo-300 dark:text-yellow-300 text-3xl font-extrabold tracking-wide"
            whileHover={{ scale: 1.1, rotate: [0, 10, -10, 0] }}
          >
            Ajjayya G V
          </motion.h1>
          <p className="text-indigo-200 dark:text-yellow-200 font-medium tracking-wide">
            QA Automation Tester | Python | Manual & Automation
          </p>
          <div className="flex items-center gap-6 text-2xl">
            {contactLinks.map(({ icon, href, label }) => (
              <motion.a
                key={label}
                href={href}
                target="_blank"
                rel="noopener noreferrer"
                aria-label={label}
                whileHover={{ scale: 1.3, rotate: 10 }}
                className="text-indigo-200 dark:text-yellow-200"
              >
                {icon}
              </motion.a>
            ))}
            <a
              href="/resume.pdf"
              target="_blank"
              rel="noopener noreferrer"
              className="px-4 py-2 bg-gradient-to-r from-indigo-600 to-purple-700 rounded-md text-white font-semibold shadow-md"
            >
              <FiDownload className="inline mr-1" /> Resume
            </a>
            <ThemeToggle theme={theme} toggleTheme={toggleTheme} />
          </div>
        </div>
      </header>

      {/* Main */}
      <main className="pt-28 pb-16 max-w-6xl mx-auto px-6 md:px-12 text-white dark:text-gray-200">
        {/* Profile */}
        <AnimatedSection>
          <h2 className="text-3xl font-bold mb-6">üë§ Profile Summary</h2>
          <p className="leading-relaxed text-lg">
            Detail-oriented QA Tester with professional experience in manual testing of web and ad-tech platforms. Skilled in functional, regression, sanity, and smoke testing, validating ad integrations, and collaborating with developers for issue resolution. Strong theoretical and hands-on knowledge of Selenium automation and API testing through personal projects, committed to transitioning into automation roles.
          </p>
        </AnimatedSection>

        {/* Skills */}
        <AnimatedSection>
          <h2 className="text-3xl font-bold mb-10">üõ†Ô∏è Skills</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            {Object.entries(skills).map(([category, items]) => (
              <div key={category} className="backdrop-blur-md bg-white/10 dark:bg-black/40 p-6 rounded-2xl border border-purple-500/40 shadow-lg">
                <h3 className="text-xl font-semibold mb-5">{category}</h3>
                <ul className="flex flex-wrap gap-3">
                  {items.map((skill) => (
                    <motion.li key={skill} whileHover={{ scale: 1.2 }} className="bg-indigo-500/20 px-4 py-2 rounded-full text-sm font-semibold cursor-pointer">
                      {skill}
                    </motion.li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </AnimatedSection>

        {/* Experience */}
        <AnimatedSection>
          <h2 className="text-3xl font-bold mb-10">üíº Professional Experience</h2>
          <div className="space-y-10">
            {experience.map(({ role, company, duration, points }) => (
              <motion.article key={role} whileHover={{ scale: 1.05 }} className="backdrop-blur-md bg-white/10 dark:bg-black/40 p-8 rounded-3xl border border-indigo-400/40 shadow-lg">
                <h3 className="text-2xl font-semibold mb-1">{role}</h3>
                <p className="italic text-indigo-300 dark:text-yellow-300 mb-4">
                  {company} ‚Ä¢ {duration}
                </p>
                <ul className="list-disc list-inside space-y-2">
                  {points.map((point, i) => <li key={i}>{point}</li>)}
                </ul>
              </motion.article>
            ))}
          </div>
        </AnimatedSection>

        {/* Projects */}
        <AnimatedSection>
          <h2 className="text-3xl font-bold mb-12">üöÄ Projects</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
            {projects.map(({ title, date, description, tech, repo, live }) => (
              <motion.article key={title} whileHover={{ scale: 1.07 }} className="backdrop-blur-md bg-white/10 dark:bg-black/40 p-8 rounded-3xl border border-purple-500/40 shadow-xl">
                <h3 className="text-2xl font-semibold mb-1">{title}</h3>
                <p className="italic text-indigo-300 dark:text-yellow-300 mb-4">{date}</p>
                <p className="mb-6 leading-relaxed">{description}</p>
                <div className="flex flex-wrap gap-3 mb-5">
                  {tech.map((t) => (
                    <motion.span key={t} whileHover={{ scale: 1.2 }} className="bg-purple-500/20 px-4 py-1 rounded-full text-sm font-semibold">{t}</motion.span>
                  ))}
                </div>
                <div className="flex gap-8">
                  {repo && (
                    <a href={repo} target="_blank" rel="noopener noreferrer" className="font-semibold hover:underline flex items-center gap-2">
                      <FiGithub /> Repo
                    </a>
                  )}
                  {live && (
                    <a href={live} target="_blank" rel="noopener noreferrer" className="font-semibold hover:underline flex items-center gap-2">
                      üîó Live
                    </a>
                  )}
                </div>
              </motion.article>
            ))}
          </div>
        </AnimatedSection>
      </main>

      {/* Scroll Top */}
      <AnimatePresence>
        {showScrollTop && (
          <motion.button
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            onClick={scrollToTop}
            aria-label="Scroll to top"
            className="fixed bottom-8 right-8 bg-indigo-600 hover:bg-indigo-800 text-white p-4 rounded-full shadow-lg z-50"
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
          >
            <FiArrowUp size={24} />
          </motion.button>
        )}
      </AnimatePresence>

      {/* Bugs */}
      <Bug size={60} variant={1} />
      <Bug size={50} variant={2} />

      {/* Footer */}
      <footer className="mt-16 py-8 text-center font-semibold tracking-wide text-indigo-200 dark:text-yellow-200">
        Made with ‚ù§Ô∏è and a little bit of üêû magic by Ajjayya G V
      </footer>

      {/* Fonts */}
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400;600;800&family=Share+Tech+Mono&display=swap');
        body {
          font-family: 'Poppins', 'Orbitron', 'Share Tech Mono', sans-serif;
        }
      `}</style>
    </>
  );
}
