import React, { useEffect, useState } from "react";
import { FiDownload, FiMail, FiGithub, FiLinkedin } from "react-icons/fi";
import { motion, AnimatePresence, useAnimation } from "framer-motion";

const contactLinks = [
  { icon: <FiMail />, href: "mailto:ajjayya2002@gmail.com", label: "Email" },
  { icon: <FiGithub />, href: "https://github.com/iamajjayya", label: "GitHub" },
  { icon: <FiLinkedin />, href: "https://linkedin.com/in/ajjayya-gv", label: "LinkedIn" },
];

const fadeInUp = {
  initial: { opacity: 0, y: 40 },
  animate: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } },
};

export default function ModernPortfolio() {
  const [showHeader, setShowHeader] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);
  const controls = useAnimation();

  // Scroll handler for header show/hide and scroll up effect
  useEffect(() => {
    function handleScroll() {
      const currentScrollY = window.scrollY;
      // Show header if scrolling up, hide if down and beyond 80px
      if (currentScrollY < 80) {
        setShowHeader(true);
      } else if (currentScrollY > lastScrollY) {
        setShowHeader(false);
      } else {
        setShowHeader(true);
      }
      setLastScrollY(currentScrollY);
    }
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [lastScrollY]);

  // Scroll up effect (small upward translate when scrolling down)
  const [scrollUpY, setScrollUpY] = useState(0);
  useEffect(() => {
    function handleScroll() {
      const offset = window.scrollY;
      setScrollUpY(offset > 10 ? -10 : 0);
    }
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Skill groups with emojis
  const skills = {
    testing: [
      "Manual Testing üìù",
      "Functional Testing ‚úîÔ∏è",
      "Regression Testing üîÑ",
      "Sanity & Smoke Testing üî•",
      "Cross-Browser Testing üåê",
      "Bug Reporting üêû",
    ],
    automation: [
      "Selenium WebDriver üï∑Ô∏è",
      "Python üêç",
      "Pytest üß™",
      "Page Object Model (POM) üìÑ",
      "API Testing with Python Requests üåê",
      "Jenkins & Docker üê≥",
      "Git & GitHub üîß",
    ],
    tools: [
      "SQL üóÑÔ∏è",
      "HTML, CSS, JavaScript üíª",
      "React.js ‚öõÔ∏è",
      "Postman üì®",
      "Jira üìã",
    ],
  };

  // Projects with emojis & links
  const projects = [
    {
      title: "Selenium Hybrid Automation Framework",
      date: "May 2025 - Jun 2025",
      description:
        "Hybrid automation framework using Page Object Model and Data-Driven approach with HTML reports and Jenkins CI/CD integration.",
      tech: ["üêç Python", "üï∑Ô∏è Selenium", "üß™ Pytest", "üìÑ HTML Reports", "üõ†Ô∏è Jenkins", "üê≥ Docker"],
      repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
    },
    {
      title: "API Automation Framework",
      date: "Aug 2025",
