import React from "react";
import { FiDownload, FiMail, FiGithub, FiLinkedin } from "react-icons/fi";
import { motion } from "framer-motion";

const fadeInUp = {
  initial: { opacity: 0, y: 20 },
  animate: { opacity: 1, y: 0, transition: { duration: 0.6 } },
};

const sectionTitleClass = "text-4xl font-extrabold text-indigo-700 mb-8";

const contactLinks = [
  { icon: <FiMail />, href: "mailto:ajjayya2002@gmail.com", label: "Email" },
  { icon: <FiGithub />, href: "https://github.com/iamajjayya", label: "GitHub" },
  { icon: <FiLinkedin />, href: "https://linkedin.com/in/ajjayya-gv", label: "LinkedIn" },
];

export default function ModernPortfolio() {
  return (
    <div className="min-h-screen bg-gradient-to-tr from-indigo-50 to-purple-100 text-gray-900 font-sans px-8 md:px-20 py-12 md:py-20 select-none">
      {/* Header */}
      <motion.header
        className="flex flex-col md:flex-row justify-between items-start md:items-center gap-6 max-w-7xl mx-auto mb-20"
        initial="initial"
        animate="animate"
        variants={fadeInUp}
      >
        <div className="max-w-xl">
          <h1 className="text-6xl font-extrabold leading-tight tracking-wide mb-2">
            Ajjayya G V
          </h1>
          <p className="text-lg font-semibold text-indigo-600 mb-1">
            QA & Python Automation Tester
          </p>
          <p className="text-gray-600 text-sm md:text-base tracking-wide">
            📍 Electronic City, Bengaluru &nbsp;|&nbsp; DOB: 05-Apr-2002 &nbsp;|&nbsp; 📞 6366325113
          </p>
        </div>
        <motion.a
          href="/resume.pdf"
          target="_blank"
          rel="noopener noreferrer"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="inline-flex items-center gap-3 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold rounded-lg px-6 py-3 shadow-lg transition-colors"
          aria-label="Download Resume"
        >
          <FiDownload size={22} /> Resume
        </motion.a>
      </motion.header>

      {/* Profile Summary */}
      <motion.section
        className="max-w-4xl mx-auto bg-white rounded-3xl shadow-xl p-12 mb-20"
        initial="initial"
        whileInView="animate"
        viewport={{ once: true }}
        variants={fadeInUp}
      >
        <h2 className={sectionTitleClass}>Profile Summary</h2>
        <p className="text-lg leading-relaxed text-gray-800 max-w-3xl">
          Detail-oriented QA Tester with 1+ year of professional experience in manual testing of web and ad-tech
          platforms. Skilled in executing functional, regression, sanity, and smoke testing, validating ad integrations,
          and collaborating with developers for issue resolution. Possess strong theoretical and hands-on knowledge of
          Python-based Selenium automation and API testing through personal projects, with a clear commitment to
          transitioning into full automation roles.
        </p>
      </motion.section>

      {/* Skills */}
      <motion.section
        className="max-w-5xl mx-auto mb-20"
        initial="initial"
        whileInView="animate"
        viewport={{ once: true }}
        variants={fadeInUp}
      >
        <h2 className={sectionTitleClass}>Skills</h2>
        <div className="flex flex-wrap gap-4 text-indigo-700 text-lg font-medium max-w-4xl mx-auto">
          {[
            "Manual Testing",
            "Functional Testing",
            "Regression Testing",
            "Sanity & Smoke Testing",
            "Cross-Browser Testing",
            "Bug Reporting",
            "Selenium WebDriver",
            "Python",
            "Pytest",
            "API Testing",
            "SQL",
            "HTML, CSS, JavaScript",
            "React.js",
            "Postman",
            "Git & GitHub",
            "Jira",
            "Jenkins",
            "Docker",
          ].map((skill) => (
            <span
              key={skill}
              className="px-5 py-2 rounded-full border-2 border-indigo-600 hover:bg-indigo-600 hover:text-white cursor-default transition"
            >
              {skill}
            </span>
          ))}
        </div>
      </motion.section>

      {/* Professional Experience */}
      <motion.section
        className="max-w-6xl mx-auto mb-20"
        initial="initial"
        whileInView="animate"
        viewport={{ once: true }}
        variants={fadeInUp}
      >
        <h2 className={sectionTitleClass}>Professional Experience</h2>

        {/* Experience Item */}
        {[
          {
            title: "QA/QC Engineer",
            company: "RTBDemand.com",
            date: "Sep 2024 – Present (Remote)",
            description:
              "Performed manual testing for ad-tech systems on desktop and mobile. Verified ad script integration, refresh functionality, and placement. Executed sanity, smoke, and regression testing across multiple environments. Logged defects, created test documentation, and collaborated with developers. Conducted basic API validations for ad delivery checks.",
          },
          {
            title: "Front-End Developer Intern",
            company: "Internplus+",
            date: "Jul 2024 – Sep 2024 (Remote)",
            description:
              "Built responsive UI components using React.js. Integrated and tested APIs in an Agile environment.",
          },
          {
            title: "Operation Executive & Catalog Data Analyst",
            company: "Zeitgeist Retail Pvt. Ltd.",
            date: "Dec 2023 – May 2024 (Internship)",
            description: "Managed catalog data and operational support tasks.",
          },
          {
            title: "Data Analyst (Part-time)",
            company: "Dhi Innovations",
            date: "May 2023 – Jul 2023",
            description: "Performed data analysis and reporting tasks.",
          },
        ].map(({ title, company, date, description }) => (
          <article
            key={title}
            className="bg-white rounded-3xl p-8 shadow-lg mb-8 hover:shadow-indigo-300 transition-shadow duration-300"
          >
            <h3 className="text-2xl font-semibold text-indigo-700 mb-1">{title}</h3>
            <p className="text-indigo-500 italic mb-2">{company} &nbsp;|&nbsp; {date}</p>
            <p className="text-gray-700 leading-relaxed">{description}</p>
          </article>
        ))}
      </motion.section>

      {/* Projects */}
      <motion.section
        className="max-w-7xl mx-auto mb-24"
        initial="initial"
        whileInView="animate"
        viewport={{ once: true }}
        variants={fadeInUp}
      >
        <h2 className={sectionTitleClass}>Projects</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
          {[
            {
              title: "Selenium Hybrid Automation Framework",
              date: "May 2025 - Jun 2025",
              description:
                "Hybrid automation framework using Page Object Model and Data-Driven approach with HTML reports and Jenkins CI/CD integration.",
              tech: ["Python", "Selenium", "Pytest", "HTML Reports", "Jenkins", "Docker"],
              repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
            },
            {
              title: "API Automation Framework",
              date: "Aug 2025",
              description:
                "Automated REST API testing with validation of endpoints, status codes, response content, and schema using pytest and requests.",
              tech: ["Python", "Pytest", "Requests", "Jenkins"],
              repo: "https://github.com/iamajjayya/api-automation",
            },
            {
              title: "E-commerce Website",
              date: "Live",
              description:
                "React.js e-commerce platform with user authentication, product listings, shopping cart, and checkout flow.",
              tech: ["React.js", "REST API", "Authentication"],
              live: "https://ecommerceajjayya.netlify.app/",
            },
            {
              title: "Gym Application",
              date: "Live",
              description:
                "Workout guide app using ExerciseDB API & YouTube API for exercise videos.",
              tech: ["React.js", "REST API", "YouTube API"],
              live: "#",
            },
          ].map(({ title, date, description, tech, repo, live }) => (
            <article
              key={title}
              className="bg-white rounded-3xl p-8 shadow-lg flex flex-col justify-between hover:shadow-indigo-400 transition-shadow duration-300"
            >
              <div>
                <h3 className="text-2xl font-semibold text-indigo-700 mb-1">{title}</h3>
                <p className="text-indigo-500 italic mb-4">{date}</p>
                <p className="text-gray-700 mb-6 leading-relaxed">{description}</p>
              </div>
              <div className="flex flex-wrap gap-2 mb-4">
                {tech.map((t) => (
                  <span
                    key={t}
                    className="inline-block bg-indigo-100 text-indigo-700 px-3 py-1 rounded-full text-sm font-semibold"
                  >
                    {t}
                  </span>
                ))}
              </div>
              <div className="flex gap-6">
                {repo && (
                  <a
                    href={repo}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-indigo-700 hover:underline font-semibold flex items-center gap-2"
                  >
                    View Repo <FiGithub />
                  </a>
                )}
                {live && (
                  <a
                    href={live}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-indigo-700 hover:underline font-semibold"
                  >
                    Live Site
                  </a>
                )}
              </div>
            </article>
          ))}
        </div>
      </motion.section>

      {/* Footer */}
      <footer className="text-center text-gray-500 text-sm mb-10 select-none">
        &copy; {new Date().getFullYear()} Ajjayya G V — QA Automation Tester
      </footer>

      {/* Contact icons fixed bottom right */}
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.8 }}
        className="fixed bottom-10 right-10 flex flex-col space-y-6 bg-white p-4 rounded-full shadow-xl"
      >
        {contactLinks.map(({ icon, href, label }) => (
          <a
            key={label}
            href={href}
            target="_blank"
            rel="noopener noreferrer"
            aria-label={label}
            className="text-indigo-600 hover:text-indigo-800 transition-colors text-2xl"
          >
            {icon}
          </a>
        ))}
      </motion.div>
    </div>
  );
