import React, { useEffect, useState } from "react";
import { FiDownload, FiMail, FiGithub, FiLinkedin } from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";

const fadeInUp = {
  initial: { opacity: 0, y: 20 },
  animate: { opacity: 1, y: 0, transition: { duration: 0.6 } },
};

const sectionTitleClass =
  "text-4xl font-extrabold text-indigo-700 mb-8 flex items-center gap-3 select-none";

const contactLinks = [
  { icon: <FiMail />, href: "mailto:ajjayya2002@gmail.com", label: "Email" },
  { icon: <FiGithub />, href: "https://github.com/iamajjayya", label: "GitHub" },
  { icon: <FiLinkedin />, href: "https://linkedin.com/in/ajjayya-gv", label: "LinkedIn" },
];

export default function ModernPortfolio() {
  // State for header show/hide on scroll
  const [showHeader, setShowHeader] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      if (currentScrollY < 50) {
        setShowHeader(true);
      } else if (currentScrollY > lastScrollY) {
        // scrolling down - hide header
        setShowHeader(false);
      } else {
        // scrolling up - show header
        setShowHeader(true);
      }
      setLastScrollY(currentScrollY);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [lastScrollY]);

  return (
    <>
      {/* Fixed Header */}
      <AnimatePresence>
        {showHeader && (
          <motion.header
            initial={{ y: -100 }}
            animate={{ y: 0, transition: { duration: 0.5, ease: "easeOut" } }}
            exit={{ y: -100, transition: { duration: 0.4, ease: "easeIn" } }}
            className="fixed top-0 left-0 right-0 bg-indigo-700 bg-opacity-95 backdrop-blur-md shadow-lg z-50"
          >
            <div className="max-w-7xl mx-auto flex justify-between items-center py-4 px-8 md:px-20 select-none">
              <h1 className="text-white text-3xl font-bold tracking-wide cursor-default">
                Ajjayya G V
              </h1>
              <motion.a
                href="/resume.pdf"
                target="_blank"
                rel="noopener noreferrer"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.95 }}
                className="inline-flex items-center gap-2 bg-indigo-500 hover:bg-indigo-600 text-white font-semibold rounded-lg px-5 py-2 shadow-lg transition-colors"
                aria-label="Download Resume"
              >
                <FiDownload size={20} /> Resume
              </motion.a>
            </div>
          </motion.header>
        )}
      </AnimatePresence>

      <main className="pt-24 min-h-screen bg-gradient-to-tr from-indigo-50 to-purple-100 text-gray-900 font-sans px-8 md:px-20 pb-20">
        {/* Profile Summary */}
        <motion.section
          className="max-w-4xl mx-auto bg-white rounded-3xl shadow-xl p-12 mb-20"
          initial="initial"
          whileInView="animate"
          viewport={{ once: true }}
          variants={fadeInUp}
        >
          <h2 className={sectionTitleClass}>
            <span role="img" aria-label="sparkles">
              ✨
            </span>
            Profile Summary
          </h2>
          <p className="text-lg leading-relaxed text-gray-800 max-w-3xl">
            Detail-oriented QA Tester with 1+ year of professional experience in manual testing of web and ad-tech
            platforms. Skilled in executing functional, regression, sanity, and smoke testing, validating ad
            integrations, and collaborating with developers for issue resolution. Possess strong theoretical and hands-on
            knowledge of Python-based Selenium automation and API testing through personal projects, with a clear
            commitment to transitioning into full automation roles.
          </p>
        </motion.section>

        {/* Skills */}
        <motion.section
          className="max-w-5xl mx-auto mb-20"
          initial="initial"
          whileInView="animate"
          viewport={{ once: true }}
          variants={fadeInUp}
        >
          <h2 className={sectionTitleClass}>
            <span role="img" aria-label="tools">
              🛠️
            </span>
            Skills
          </h2>
          <div className="flex flex-wrap gap-4 text-indigo-700 text-lg font-medium max-w-4xl mx-auto">
            {[
              "Manual Testing",
              "Functional Testing",
              "Regression Testing",
              "Sanity & Smoke Testing",
              "Cross-Browser Testing",
              "Bug Reporting",
              "Selenium WebDriver",
              "Python",
              "Pytest",
              "API Testing",
              "SQL",
              "HTML, CSS, JavaScript",
              "React.js",
              "Postman",
              "Git & GitHub",
              "Jira",
              "Jenkins",
              "Docker",
            ].map((skill) => (
              <motion.span
                key={skill}
                whileHover={{ scale: 1.1, color: "#4338CA" }} // Indigo-700 dark hover
                className="px-5 py-2 rounded-full border-2 border-indigo-600 cursor-default hover:bg-indigo-100 transition-colors font-semibold select-none"
              >
                {skill}
              </motion.span>
            ))}
          </div>
        </motion.section>

        {/* Professional Experience */}
        <motion.section
          className="max-w-6xl mx-auto mb-20"
          initial="initial"
          whileInView="animate"
          viewport={{ once: true }}
          variants={fadeInUp}
        >
          <h2 className={sectionTitleClass}>
            <span role="img" aria-label="briefcase">
              💼
            </span>
            Professional Experience
          </h2>

          {/* Experience Items */}
          {[
            {
              title: "QA/QC Engineer",
              company: "RTBDemand.com",
              date: "Sep 2024 – Present (Remote)",
              description:
