import React from "react";
import { FiDownload, FiMail, FiGithub, FiLinkedin } from "react-icons/fi";
import { motion } from "framer-motion";

const colors = {
  primaryBlue: "#2563EB",
  darkBlue: "#1E40AF",
  lightBlue: "#DBEAFE",
  slateGray: "#64748B",
  softWhite: "#F9FAFB",
};

const fadeInUp = {
  initial: { opacity: 0, y: 30 },
  animate: { opacity: 1, y: 0, transition: { duration: 0.6 } },
};

const skills = [
  { emoji: "üß™", label: "Manual Testing" },
  { emoji: "‚öôÔ∏è", label: "Automation Testing" },
  { emoji: "üêç", label: "Python" },
  { emoji: "üß∞", label: "Selenium WebDriver" },
  { emoji: "üß©", label: "Pytest" },
  { emoji: "üì°", label: "API Testing" },
  { emoji: "üõ†Ô∏è", label: "Jenkins & CI/CD" },
  { emoji: "üê≥", label: "Docker" },
  { emoji: "üåê", label: "React.js" },
  { emoji: "üóÉÔ∏è", label: "SQL" },
];

const education = [
  {
    degree: "MCA",
    school: "JAIN (Deemed-to-be University)",
    period: "2023 ‚Äì 2025",
  },
  {
    degree: "BCA",
    school: "Administrative Management College",
    period: "2019 ‚Äì 2022",
    cgpa: "8.38",
  },
  {
    degree: "PUC",
    school: "Loyola Composite PU College",
    period: "2017 ‚Äì 2019",
    marks: "90.4%",
  },
];

const experience = [
  {
    role: "QA/QC Engineer",
    company: "RTBDemand.com",
    period: "Sep 2024 ‚Äì Present",
    description:
      "Manual testing for ad-tech platforms, ad script validation, regression and smoke testing, API validations.",
  },
  {
    role: "Front-End Developer Intern",
    company: "Internplus+",
    period: "Jul 2024 ‚Äì Sep 2024",
    description: "Built responsive React.js components and API integrations in Agile environment.",
  },
  {
    role: "Operation Executive & Catalog Data Analyst",
    company: "Zeitgeist Retail Pvt. Ltd.",
    period: "Dec 2023 ‚Äì May 2024",
    description: "Managed catalog data and performed operational support.",
  },
  {
    role: "Data Analyst (Part-time)",
    company: "Dhi Innovations",
    period: "May 2023 ‚Äì Jul 2023",
    description: "Performed data analysis and reporting tasks.",
  },
];

const projects = [
  {
    title: "Python Hybrid Automation Framework",
    period: "May 2025 - Jun 2025",
    points: [
      "Selenium WebDriver for browser automation",
      "Page Object Model (POM) design pattern for maintainability",
      "Data-Driven Testing (DDT) for multiple data sets",
      "Parallel test execution with pytest-xdist",
      "Cross-browser testing support",
      "CI/CD integration with Jenkins & Docker",
    ],
    tech: ["Python", "Selenium", "Pytest", "Jenkins", "Docker"],
    repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
  },
  {
    title: "API Automation Framework",
    period: "Aug 2025",
    points: [
      "Automated FakeStore API endpoint validations",
      "Schema validation for responses",
