import React, { useEffect, useState } from "react";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiArrowUp,
} from "react-icons/fi";
import { motion, AnimatePresence, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";

const contactLinks = [
  { icon: <FiMail />, href: "mailto:ajjayya2002@gmail.com", label: "Email" },
  { icon: <FiGithub />, href: "https://github.com/iamajjayya", label: "GitHub" },
  { icon: <FiLinkedin />, href: "https://linkedin.com/in/ajjayya-gv", label: "LinkedIn" },
];

const skills = {
  Testing: [
    "üìù Manual Testing",
    "‚úîÔ∏è Functional Testing",
    "üîÑ Regression Testing",
    "üî• Sanity & Smoke Testing",
    "üåê Cross-Browser Testing",
    "üêû Bug Reporting",
    "üìä SDLC (Software Development Life Cycle)",
    "üõ†Ô∏è STLC (Software Testing Life Cycle)",
    "üóíÔ∏è Test Case Design & Execution",
    "üîç Defect Tracking & Reporting",
    "üß™ Exploratory Testing",
  ],
  Automation: [
    "üêç Python",
    "üï∑Ô∏è Selenium WebDriver",
    "üß™ Pytest",
    "üìÑ Page Object Model (POM)",
    "üåê API Testing (Requests)",
    "‚öôÔ∏è Jenkins CI/CD",
    "üîß Git & GitHub",
    "üöÄ Agile Methodology",
  ],
  Tools: [
    "üóÑÔ∏è SQL",
    "üíª HTML, CSS, JavaScript",
    "‚öõÔ∏è React.js",
    "üì® Postman",
    "üìã Jira",
  ],
};

const projects = [
  {
    title: "Selenium Hybrid Automation Framework",
    date: "May 2025 - Jun 2025",
    description:
      "Hybrid automation framework using Page Object Model & Data-Driven approach with HTML reports and Jenkins CI/CD integration.",
    tech: ["üêç Python", "üï∑Ô∏è Selenium", "üß™ Pytest", "üìÑ HTML Reports", "üõ†Ô∏è Jenkins"],
    repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
  },
  {
    title: "API Automation Framework",
    date: "Aug 2025",
    description:
      "Automated REST API testing with validation of endpoints, status codes, and response content.",
    tech: ["üêç Python", "üß™ Pytest", "üåê Requests", "üõ†Ô∏è Jenkins"],
    repo: "https://github.com/iamajjayya/api-automation",
  },
  {
    title: "E-commerce Website",
    date: "Live",
    description:
      "React.js e-commerce platform with user authentication, product listings, shopping cart, and checkout flow.",
    tech: ["‚öõÔ∏è React.js", "üîê Authentication", "üåê REST API"],
    live: "https://ecommerceajjayya.netlify.app/",
  },
  {
    title: "Gym Application",
    date: "Live",
    description:
      "Workout guide app using ExerciseDB and YouTube APIs for exercise videos and routines.",
    tech: ["‚öõÔ∏è React.js", "üåê REST API", "‚ñ∂Ô∏è YouTube API"],
    live: "https://your-gym-app-link.netlify.app/",
  },
];

const experience = [
  {
    role: "QA/QC Engineer",
    company: "RTBDemand.com",
    duration: "Sep 2024 ‚Äì Present (Remote)",
    points: [
      "Performed manual testing for ad-tech systems on desktop and mobile.",
      "Verified ad script integration, refresh functionality, and placement.",
      "Executed sanity, smoke, and regression testing across multiple environments.",
      "Logged defects, created test documentation, collaborated with developers.",
      "Conducted basic API validations for ad delivery checks.",
    ],
  },
  {
    role: "Front-End Developer Intern",
    company: "Internplus+",
    duration: "Jul 2024 ‚Äì Sep 2024 (Remote)",
    points: [
      "Built responsive UI components using React.js.",
      "Integrated and tested APIs in an Agile environment.",
    ],
  },
];

// Quotes for mascot tooltip
const bugQuotes = [
  "üêû Found a bug? Let‚Äôs squash it! üßπ",
  "üîç Testing is my superpower!",
  "üí° 'If debugging is the process of removing bugs, then programming must be the process of putting them in.' ‚Äî Edsger Dijkstra",
  "üéØ Quality is not an act, it‚Äôs a habit!",
  "üêõ Bugs today, features tomorrow!",
];

// Animate section on scroll hook
function AnimatedSection({ children }) {
  const [ref, inView] = useInView({ triggerOnce: true, threshold: 0.15 });
  return (
    <motion.section
      ref={ref}
      initial={{ opacity: 0, y: 40 }}
      animate={inView ? { opacity: 1, y: 0 } : {}}
      transition={{ duration: 0.7, ease: "easeOut" }}
      className="mb-20"
    >
      {children}
    </motion.section>
  );
}

// Floating mascot with funny tooltip & random movement, with props to position and unique keys
function FloatingMascot({ style, position }) {
  const controls = useAnimation();
  const [showTooltip, setShowTooltip] = useState(false);
  const [quote, setQuote] = useState("");

  useEffect(() => {
    const interval = setInterval(() => {
      controls.start({
        x: Math.random() * 60 - 30,
        y: Math.random() * 60 - 30,
        transition: { duration: 5 + Math.random() * 5, ease: "easeInOut" },
      });
    }, 7000);
    return () => clearInterval(interval);
  }, [controls]);

  const toggleTooltip = () => {
    setQuote(bugQuotes[Math.floor(Math.random() * bugQuotes.length)]);
    setShowTooltip((v) => !v);
  };

  return (
    <motion.div
      animate={controls}
      initial={{ x: 0, y: 0 }}
      style={style}
      className={`fixed z-50 cursor-pointer select-none ${position}`}
      whileHover={{ scale: 1.4, rotate: [0, 15, -15, 0] }}
      whileTap={{ scale: 1.6, rotate: 360 }}
      onClick={toggleTooltip}
      aria-label="Testing bug mascot"
      title="Click me for testing fun!"
    >
      <span className="text-5xl md:text-6xl">üêû</span>

      <AnimatePresence>
        {showTooltip && (
          <motion.div
            key="tooltip"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 10 }}
            transition={{ duration: 0.3 }}
            className="absolute bottom-full mb-3 right-0 bg-indigo-600 text-white rounded-lg px-4 py-2 max-w-xs text-sm shadow-lg select-none"
          >
            {quote}
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
}

export default function Portfolio() {
  const [showScrollTop, setShowScrollTop] = useState(false);

  useEffect(() => {
    const onScroll = () => setShowScrollTop(window.scrollY > 300);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: "smooth" });

  return (
    <>
      {/* Floating mascots */}
      <FloatingMascot style={{ bottom: 40, right: 40 }} position="" />
      <FloatingMascot style={{ top: 100, left: 30 }} position="md:top-20 md:left-10" />
      <FloatingMascot style={{ top: 60, right: 200 }} position="hidden md:block" />
      <FloatingMascot style={{ bottom: 150, left: 60 }} position="hidden md:block" />

      {/* Header */}
      <header className="fixed top-0 left-0 right-0 bg-indigo-50/95 backdrop-blur-md shadow-lg z-50 border-b border-indigo-200">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between px-6 py-4 gap-4 md:gap-0">
          <div className="flex items-center gap-3 select-none cursor-default">
            <motion.h1
              className="text-indigo-700 text-3xl font-extrabold tracking-wide"
              whileHover={{ scale: 1.1, rotate: [0, 10, -10, 0] }}
              transition={{ duration: 1 }}
            >
              Ajjayya G V
            </motion.h1>
            <motion.span
              className="text-3xl"
              role="img"
              aria-label="waving robot"
              title="Hello! üëã"
              animate={{ rotate: [0, 20, -20, 0] }}
              transition={{ duration: 2, repeat: Infinity, repeatDelay: 2 }}
            >
              ü§ñ
            </motion.span>
          </div>
          <p className="text-indigo-700 font-medium tracking-wide md:ml-6 text-center md:text-left text-sm md:text-base">
            QA Automation Tester | Python | Manual & Automation
          </p>
          <div className="flex items-center gap-6 text-indigo-700 text-2xl justify-center md:justify-start">
            {contactLinks.map(({ icon, href, label }) => (
              <motion.a
                key={label}
                href={href}
                target="_blank"
                rel="noopener noreferrer"
                aria-label={label}
                whileHover={{ scale: 1.3, color: "#7c3aed", rotate: 10 }}
                whileTap={{ scale: 0.9, rotate: -10 }}
                transition={{ type: "spring", stiffness: 300 }}
                className="transition-colors"
              >
                {icon}
              </motion.a>
            ))}
            <a
              href="/resume.pdf"
              target="_blank"
              rel="noopener noreferrer"
              className="ml-4 px-4 py-2 bg-indigo-600 hover:bg-indigo-700 rounded-md text-white font-semibold shadow-md transition"
              aria-label="Download Resume"
            >
              <FiDownload className="inline mr-1" /> Resume
            </a>
          </div>
        </div>
      </header>

      {/* Main content with padding top to avoid header overlap on mobile */}
      <main className="pt-28 pb-16 max-w-6xl mx-auto px-6 md:px-12">
        {/* Profile Summary */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-6 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            üë§ Profile Summary
          </motion.h2>
          <p className="text-gray-700 leading-relaxed text-lg">
            Detail-oriented QA Tester with professional experience in manual testing of web and ad-tech platforms.
            Skilled in functional, regression, sanity, and smoke testing, validating ad integrations, and collaborating
            with developers for issue resolution. Strong theoretical and hands-on knowledge of Selenium automation and
            API testing through personal projects, committed to transitioning into automation roles.
          </p>
        </AnimatedSection>

        {/* Skills */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-10 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            üõ†Ô∏è Skills
          </motion.h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            {Object.entries(skills).map(([category, items]) => (
              <div key={category} className="flex flex-col">
                <h3 className="text-indigo-600 text-xl font-semibold mb-5">{category}</h3>
                <ul className="flex flex-wrap gap-3">
                  {items.map((skill) => (
                    <motion.li
                      key={skill}
                      whileHover={{
                        scale: 1.3,
                        rotate: [0, 10, -10, 0],
                        color: "#7c3aed",
                        textShadow: "0 0 8px #7c3aed",
                      }}
                      whileTap={{ scale: 0.9, rotate: 0 }}
                      className="bg-indigo-100 text-indigo-800 px-4 py-2 rounded-full text-sm font-semibold cursor-pointer select-none shadow-md transition-colors duration-300"
                    >
                      {skill}
                    </motion.li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </AnimatedSection>

        {/* Experience */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-10 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            üíº Professional Experience
          </motion.h2>
          <div className="space-y-10">
            {experience.map(({ role, company, duration, points }) => (
              <motion.article
                key={role}
                whileHover={{
                  scale: 1.05,
                  boxShadow: "0 10px 30px rgba(124, 58, 237, 0.4)",
                }}
                className="bg-white p-8 rounded-3xl shadow-md transition-shadow duration-300"
              >
                <h3 className="text-indigo-700 text-2xl font-semibold mb-1">{role}</h3>
                <p className="italic text-indigo-500 mb-4">
                  {company} ‚Ä¢ {duration}
                </p>
                <ul className="list-disc list-inside text-gray-700 space-y-2">
                  {points.map((point, i) => (
                    <li key={i}>{point}</li>
                  ))}
                </ul>
              </motion.article>
            ))}
          </div>
        </AnimatedSection>

        {/* Projects */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-12 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            üöÄ Projects
          </motion.h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
            {projects.map(({ title, date, description, tech, repo, live }) => (
              <motion.article
                key={title}
                whileHover={{
                  scale: 1.07,
                  boxShadow: "0 15px 30px rgba(124, 58, 237, 0.5)",
                }}
                className="bg-white rounded-3xl shadow-lg p-8 flex flex-col justify-between transition-shadow duration-300"
              >
                <div>
                  <h3 className="text-indigo-800 text-2xl font-semibold mb-1">
                    {title}
                  </h3>
                  <p className="text-indigo-500 italic mb-4">{date}</p>
                  <p className="text-gray-700 mb-6 leading-relaxed">{description}</p>
                </div>
                <div className="flex flex-wrap gap-3 mb-5">
                  {tech.map((t) => (
                    <motion.span
                      key={t}
                      whileHover={{ scale: 1.4, rotate: [0, 10, -10, 0] }}
                      whileTap={{ scale: 0.9 }}
                      className="bg-indigo-100 text-indigo-800 px-4 py-1 rounded-full text-sm font-semibold cursor-pointer select-none shadow-sm"
                    >
                      {t}
                    </motion.span>
                  ))}
                </div>
                <div className="flex gap-8">
                  {repo && (
                    <a
                      href={repo}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-700 font-semibold hover:underline flex items-center gap-2"
                    >
                      <FiGithub /> Repo
                    </a>
                  )}
                  {live && (
                    <a
                      href={live}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-700 font-semibold hover:underline flex items-center gap-2"
                    >
                      üîó Live
                    </a>
                  )}
                </div>
              </motion.article>
            ))}
          </div>
        </AnimatedSection>
      </main>

      {/* Footer */}
      <footer className="bg-indigo-50 border-t border-indigo-200 py-8 mt-20">
        <div className="max-w-6xl mx-auto px-6 md:px-12 flex flex-col md:flex-row justify-between items-center gap-6">
          <p className="text-indigo-700 font-semibold text-center md:text-left">
            &copy; {new Date().getFullYear()} Ajjayya G V ‚Äî QA Automation Tester ü§ñ
          </p>
          <div className="flex items-center gap-8 text-indigo-700 text-2xl">
            {contactLinks.map(({ icon, href, label }) => (
              <a
                key={label}
                href={href}
                target="_blank"
                rel="noopener noreferrer"
                aria-label={label}
                className="hover:text-indigo-900 transition-colors"
              >
                {icon}
              </a>
            ))}
          </div>
        </div>
      </footer>

      {/* Scroll to top button */}
      <AnimatePresence>
        {showScrollTop && (
          <motion.button
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0, rotate: [0, 15, -15, 0] }}
            exit={{ opacity: 0, y: 20 }}
            onClick={scrollToTop}
            aria-label="Scroll to top"
            className="fixed bottom-8 right-8 bg-indigo-700 hover:bg-indigo-800 text-white p-4 rounded-full shadow-lg z-50 transition-colors duration-300"
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
          >
            <FiArrowUp size={24} />
          </motion.button>
        )}
      </AnimatePresence>
    </>
  );
}
