import React, { useState, useEffect } from "react";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiSun,
  FiMoon,
} from "react-icons/fi";
import { FaDocker, FaPython, FaReact } from "react-icons/fa";
import { SiJenkins, SiPostman } from "react-icons/si";
import { motion, useAnimation } from "framer-motion";
import { useInView } from "react-intersection-observer";

export default function App() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) document.documentElement.classList.add("dark");
    else document.documentElement.classList.remove("dark");
  }, [darkMode]);

  // Scroll animation hook helper
  const ScrollAnimation = ({ children }) => {
    const controls = useAnimation();
    const [ref, inView] = useInView({
      threshold: 0.2,
      triggerOnce: true,
    });

    useEffect(() => {
      if (inView) controls.start("visible");
    }, [controls, inView]);

    return (
      <motion.div
        ref={ref}
        initial="hidden"
        animate={controls}
        variants={{
          visible: { opacity: 1, y: 0, transition: { duration: 0.8 } },
          hidden: { opacity: 0, y: 30 },
        }}
        className="w-full"
      >
        {children}
      </motion.div>
    );
  };

  const projects = [
    {
      title: "üêç Python Hybrid Automation Framework",
      period: "May 2025 - Jun 2025",
      descPoints: [
        "Built scalable automation using Selenium WebDriver & Python.",
        "Implemented Page Object Model (POM) for clean, reusable code.",
        "Data-driven tests using pytest & CSV/JSON files for robustness.",
        "Parallel runs with pytest-xdist ‚ö° & cross-browser support.",
        "Integrated CI/CD pipelines with Jenkins & Docker containerized tests.",
      ],
      techStack: ["Python", "Selenium", "pytest", "POM", "Jenkins", "Docker"],
      repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
    },
    {
      title: "‚öôÔ∏è API Automation Framework",
      period: "Aug 2025 - Aug 2025",
      descPoints: [
        "Automated REST API validation with pytest & requests.",
        "Schema validation üìú to ensure API response correctness.",
        "Extensive positive & negative tests for reliability.",
        "Generated HTML test reports üìù & integrated with Jenkins CI.",
        "Version control & collaboration using GitHub.",
      ],
      techStack: ["Python", "pytest", "Requests", "JSON Schema", "Jenkins"],
      repo: "https://github.com/iamajjayya/api-automation",
    },
    {
      title: "üõí E-commerce Website",
      period: "Live (React.js)",
      descPoints: [
        "Built a responsive e-commerce platform with React.js.",
        "User authentication, product listing, cart & checkout flow.",
        "Focus on UI/UX for seamless shopping experience.",
        <>
          Live site:{" "}
          <a
            key="ecomlink"
            href="https://ecommerceajjayya.netlify.app/"
            target="_blank"
            rel="noreferrer"
            className="text-gold hover:underline"
          >
            ecommerceajjayya.netlify.app
          </a>
        </>,
      ],
      techStack: ["React.js", "REST API", "Authentication", "UI/UX"],
      repo: null,
    },
    {
      title: "üèãÔ∏è Gym Workout App",
      period: "React.js & YouTube API",
      descPoints: [
        "Workout guide with ExerciseDB & YouTube API integration.",
        "Dynamic workout recommendations with rich video content.",
      ],
      techStack: ["React.js", "REST API", "YouTube API"],
      repo: "https://github.com/iamajjayya/gym-app",
    },
  ];

  const skills = [
    {
      category: "üß™ Testing",
      items: [
        "Manual Testing (1 year experience)",
        "Functional & Regression",
        "Sanity & Smoke",
        "Cross-Browser Testing",
        "Bug Reporting",
      ],
    },
    {
      category: "ü§ñ Automation",
      items: [
        "Python (Expert)",
        "Selenium WebDriver",
        "pytest",
        "POM Design",
        "API Testing with Requests",
        "pytest-html reporting",
      ],
    },
    {
      category: "üõ†Ô∏è Tools & Tech",
      items: [
        "Docker üê≥",
        "Git/GitHub",
        "Jenkins CI/CD",
        "Postman",
        "Jira",
        "React.js",
        "SQL",
        "HTML/CSS/JavaScript",
      ],
    },
  ];

  const experience = [
    {
      role: "QA/QC Engineer",
      company: "RTBDemand.com",
      period: "Sep 2024 ‚Äì Present (Remote)",
      points: [
        "Performed manual & exploratory testing on ad-tech platforms.",
        "Verified ad script integration, placement & refresh cycles.",
        "Executed sanity, regression & smoke tests with detailed reports.",
        "Collaborated with developers to resolve bugs & optimize delivery.",
        "Basic API validation for ad delivery & monitoring.",
      ],
    },
    {
      role: "Front-End Developer Intern",
      company: "Internplus+",
      period: "Jul 2024 ‚Äì Sep 2024 (Remote)",
      points: [
        "Developed responsive UI components using React.js.",
        "Integrated and tested APIs in Agile sprints.",
      ],
    },
    {
      role: "Data Analyst (Part-time)",
      company: "Dhi Innovations",
      period: "May 2023 - Jul 2023",
      points: ["Supported data-driven operations and reporting."],
    },
    {
      role: "Operation Executive & Catalogue Data Analyst (Internship)",
      company: "Zeitgeist Retail Pvt. Ltd.",
      period: "Dec 2023 - May 2024",
      points: ["Managed catalogue data & supported retail operations."],
    },
  ];

  const education = [
    { degree: "MCA", institute: "JAIN University", period: "2023‚Äì2025" },
    {
      degree: "BCA",
      institute: "Administrative Management College",
      period: "2019‚Äì2022",
      cgpa: "8.38",
    },
    { degree: "PUC", institute: "Loyola Composite PU College", period: "2017‚Äì2019", percentage: "90.4%" },
    { degree: "SSLC", institute: "Loyola Composite School", period: "2016‚Äì2017", percentage: "64.48%" },
  ];

  return (
    <div className="min-h-screen bg-cream transition-colors duration-700 font-body text-charcoal px-6 py-12 sm:px-10 lg:px-20 max-w-screen-xl mx-auto">
      {/* Dark Mode Toggle */}
      <div className="flex justify-end mb-8">
        <button
          onClick={() => setDarkMode((d) => !d)}
          aria-label="Toggle Dark Mode"
          className="p-2 rounded-full bg-burgundy text-cream hover:bg-gold hover:text-charcoal transition"
        >
          {darkMode ? <FiSun size={22} /> : <FiMoon size={22} />}
        </button>
      </div>

      {/* Sticky Header */}
      <motion.header
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="sticky top-0 bg-cream dark:bg-gray-900 z-50 py-6 mb-14 flex flex-col sm:flex-row items-start sm:items-center justify-between gap-6 border-b border-burgundy border-opacity-20 shadow-sm"
      >
        <div>
          <h1 className="text-4xl sm:text-5xl font-extrabold text-burgundy font-heading leading-tight select-text">
            Ajjayya G V <br />
            <span className="text-xl sm:text-2xl font-semibold text-gold select-text">
              QA & Python Automation Tester
            </span>
          </h1>
          <p className="mt-2 text-base sm:text-lg font-medium select-text max-w-md">
            üìç Electronic City, Bengaluru &nbsp;&nbsp;|&nbsp;&nbsp; üìÖ 05-Apr-2002 <br />
            üìû 6366325113 &nbsp;&nbsp;|&nbsp;&nbsp; üìß ajjayya2002@gmail.com
          </p>
        </div>
        <motion.a
          href="/resume.pdf"
          target="_blank"
          rel="noopener noreferrer"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          className="inline-flex items-center gap-3 px-6 py-3 bg-burgundy text-cream rounded-xl shadow-lg font-semibold tracking-wide shadow-gold/50 select-text"
        >
          <FiDownload size={20} /> Download Resume
        </motion.a>
      </motion.header>

      {/* Profile Summary */}
      <ScrollAnimation>
        <section className="bg-white dark:bg-gray-800 rounded-3xl p-8 shadow-lg mb-14">
          <h2 className="text-3xl font-bold text-burgundy mb-4 font-heading select-text">
            üëã Profile Summary
          </h2>
          <p className="text-charcoal dark:text-gray-200 text-lg leading-relaxed select-text max-w-3xl">
            I‚Äôm a <strong>detail-driven QA Tester</strong> with{" "}
            <span className="text-burgundy font-semibold">1 year</span> of solid manual testing experience and a{" "}
            <span className="text-burgundy font-semibold">strong expertise in Python automation</span>. I build robust
            automation frameworks and APIs with clean, maintainable code to ensure quality at speed. Passionate about
            bridging manual and automation testing for full coverage and continuous delivery.
          </p>
        </section>
      </ScrollAnimation>

      {/* Skills */}
      <ScrollAnimation>
        <section className="mb-14">
          <h3 className="text-3xl font-extrabold mb-8 text-burgundy font-heading select-text">üõ†Ô∏è Skills & Tools</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            {skills.map(({ category, items }) => (
              <div
                key={category}
                className="bg-white dark:bg-gray-800 rounded-2xl p-6 shadow-md select-text"
              >
                <h4 className="text-xl font-bold mb-5 text-gold font-heading">{category}</h4>
                <ul className="list-disc list-inside space-y-2 text-charcoal dark:text-gray-300 text-lg font-medium">
                  {items.map((skill) => (
                    <li key={skill} className="flex items-center gap-2">
                      {skill.includes("Docker") ? (
                        <FaDocker className="text-blue-600" />
                      ) : skill.includes("Python") ? (
                        <FaPython className="text-yellow-500" />
                      ) : skill.includes("React") ? (
                        <FaReact className="text-cyan-500" />
                      ) : skill.includes("Jenkins") ? (
                        <SiJenkins className="text-red-600" />
                      ) : skill.includes("Postman") ? (
                        <SiPostman className="text-orange-500" />
                      ) : null}
                      <span>{skill}</span>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </section>
      </ScrollAnimation>

      {/* Projects */}
      <ScrollAnimation>
        <section className="mb-14">
          <h3 className="text-3xl font-extrabold mb-10 text-burgundy font-heading select-text">üöÄ Projects</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
            {projects.map(({ title, period, descPoints, techStack, repo }) => (
              <motion.article
                key={title}
                whileHover={{ scale: 1.03, boxShadow: "0 15px 30px rgba(123, 30, 40, 0.3)" }}
                className="bg-white dark:bg-gray-800 rounded-3xl p-7 shadow-lg cursor-pointer flex flex-col select-text"
              >
                <div className="flex justify-between items-center mb-4">
                  <h4 className="text-2xl font-bold text-burgundy font-heading">{title}</h4>
                  {period && (
                    <span className="text-sm font-semibold text-gray-500 dark:text-gray-400">{period}</span>
                  )}
                </div>
                <ul className="text-charcoal dark:text-gray-300 list-disc list-inside mb-5 space-y-1 text-lg">
                  {descPoints.map((pt, idx) => (
                    <li key={idx}>{pt}</li>
                  ))}
                </ul>
                <div className="flex flex-wrap gap-2 mb-4">
                  {techStack.map((tag) => (
                    <span
                      key={tag}
                      className="text-xs px-3 py-1 rounded-full bg-gold bg-opacity-20 text-gold font-semibold"
                    >
                      {tag}
                    </span>
                  ))}
                </div>
                {repo && (
                  <a
                    href={repo}
                    target="_blank"
                    rel="noreferrer"
                    className="inline-flex items-center gap-2 text-burgundy font-semibold hover:underline"
                  >
                    View Repo <FiGithub size={18} />
                  </a>
                )}
              </motion.article>
            ))}
          </div>
        </section>
      </ScrollAnimation>

      {/* Experience */}
      <ScrollAnimation>
        <section className="mb-14">
          <h3 className="text-3xl font-extrabold mb-10 text-burgundy font-heading select-text">üíº Professional Experience</h3>
          <div className="space-y-8 max-w-4xl mx-auto">
            {experience.map(({ role, company, period, points }, idx) => (
              <article
                key={idx}
                className="bg-white dark:bg-gray-800 rounded-3xl p-7 shadow-lg select-text"
              >
                <h4 className="text-xl font-bold text-burgundy font-heading mb-1">
                  {role} @ <span className="font-normal text-charcoal dark:text-gray-300">{company}</span>
                </h4>
                <p className="text-sm font-semibold text-gray-500 dark:text-gray-400 mb-4">
                  {period}
                </p>
                <ul className="list-disc list-inside text-charcoal dark:text-gray-300 space-y-1 text-lg">
                  {points.map((pt, i) => (
                    <li key={i}>{pt}</li>
                  ))}
                </ul>
              </article>
            ))}
          </div>
        </section>
      </ScrollAnimation>

      {/* Education */}
      <ScrollAnimation>
        <section className="mb-20">
          <h3 className="text-3xl font-extrabold mb-10 text-burgundy font-heading select-text">üéì Education</h3>
          <div className="space-y-6 max-w-3xl mx-auto">
            {education.map(({ degree, institute, period, cgpa, percentage }, idx) => (
              <article
                key={idx}
                className="bg-white dark:bg-gray-800 rounded-3xl p-6 shadow-md select-text"
              >
                <h4 className="text-lg font-bold text-burgundy font-heading">{degree}</h4>
                <p className="text-sm font-semibold text-charcoal dark:text-gray-400">{institute}</p>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  {period} {cgpa && `‚Äî CGPA: ${cgpa}`} {percentage && `‚Äî ${percentage}`}
                </p>
              </article>
            ))}
          </div>
        </section>
      </ScrollAnimation>

      {/* Footer */}
      <motion.footer
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5 }}
        className="bg-white dark:bg-gray-800 rounded-3xl p-8 shadow-lg flex flex-col sm:flex-row items-center justify-between gap-6 select-text"
      >
        <div>
          <h4 className="font-bold text-burgundy font-heading">Let's Collaborate! ü§ù</h4>
          <p className="text-charcoal dark:text-gray-300 text-lg max-w-md">
            Manual & automation testing | Python automation frameworks | CI/CD pipelines
          </p>
        </div>
        <div className="flex gap-8 text-burgundy text-2xl">
          <a href="mailto:ajjayya2002@gmail.com" className="hover:text-gold transition" aria-label="Email">
            <FiMail />
          </a>
          <a
            href="https://github.com/iamajjayya"
            target="_blank"
            rel="noreferrer"
            className="hover:text-gold transition"
            aria-label="GitHub"
          >
            <FiGithub />
          </a>
          <a
            href="https://linkedin.com/in/ajjayya-gv"
            target="_blank"
            rel="noreferrer"
            className="hover:text-gold transition"
            aria-label="LinkedIn"
          >
            <FiLinkedin />
          </a>
        </div>
      </motion.footer>
    </div>
  );
}
