import React, { useEffect, useState } from "react";
import { FiDownload, FiMail, FiGithub, FiLinkedin, FiArrowUp } from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";

const contactLinks = [
  { icon: <FiMail />, href: "mailto:ajjayya2002@gmail.com", label: "Email" },
  { icon: <FiGithub />, href: "https://github.com/iamajjayya", label: "GitHub" },
  { icon: <FiLinkedin />, href: "https://linkedin.com/in/ajjayya-gv", label: "LinkedIn" },
];

const skills = {
  Testing: [
    "📝 Manual Testing",
    "✔️ Functional Testing",
    "🔄 Regression Testing",
    "🔥 Sanity & Smoke Testing",
    "🌐 Cross-Browser Testing",
    "🐞 Bug Reporting",
  ],
  Automation: [
    "🐍 Python",
    "🕷️ Selenium WebDriver",
    "🧪 Pytest",
    "📄 Page Object Model (POM)",
    "🌐 API Testing with Requests",
    "🐳 Docker & Jenkins",
    "🔧 Git & GitHub",
  ],
  Tools: [
    "🗄️ SQL",
    "💻 HTML, CSS, JavaScript",
    "⚛️ React.js",
    "📨 Postman",
    "📋 Jira",
  ],
};

const projects = [
  {
    title: "Selenium Hybrid Automation Framework",
    date: "May 2025 - Jun 2025",
    description:
      "Hybrid automation framework using Page Object Model & Data-Driven approach with HTML reports and Jenkins CI/CD integration.",
    tech: ["🐍 Python", "🕷️ Selenium", "🧪 Pytest", "📄 HTML Reports", "🛠️ Jenkins", "🐳 Docker"],
    repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
  },
  {
    title: "API Automation Framework",
    date: "Aug 2025",
    description:
      "Automated REST API testing with validation of endpoints, status codes, and response content.",
    tech: ["🐍 Python", "🧪 Pytest", "🌐 Requests", "🛠️ Jenkins"],
    repo: "https://github.com/iamajjayya/api-automation",
  },
  {
    title: "E-commerce Website",
    date: "Live",
    description:
      "React.js e-commerce platform with user authentication, product listings, shopping cart, and checkout flow.",
    tech: ["⚛️ React.js", "🔐 Authentication", "🌐 REST API"],
    live: "https://ecommerceajjayya.netlify.app/",
  },
];

export default function Portfolio() {
  const [showScrollTop, setShowScrollTop] = useState(false);

  // Show scroll-to-top button after scrolling 300px
  useEffect(() => {
    const onScroll = () => setShowScrollTop(window.scrollY > 300);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  // Scroll to top smoothly
  const scrollToTop = () => window.scrollTo({ top: 0, behavior: "smooth" });

  return (
    <>
      {/* Fixed Header */}
      <header className="fixed top-0 left-0 right-0 bg-indigo-900 shadow-lg z-50">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between px-6 py-4">
          <div className="flex items-center gap-3">
            <h1 className="text-white text-3xl font-extrabold tracking-widest select-none cursor-default">
              Ajjayya G V
            </h1>
            <div className="hidden md:block w-20 h-1 bg-indigo-400 rounded-full animate-pulse"></div>
            <p className="text-indigo-300 font-medium tracking-wide select-none">
              QA Automation Tester | Python | Manual & Automation
            </p>
          </div>
          <div className="flex items-center gap-7 text-indigo-300 text-2xl">
            {contactLinks.map(({ icon, href, label }) => (
              <a
                key={label}
                href={href}
                target="_blank"
                rel="noopener noreferrer"
                aria-label={label}
                className="hover:text-indigo-400 transition-colors duration-300"
              >
                {icon}
              </a>
            ))}
            <a
              href="/resume.pdf"
              target="_blank"
              rel="noopener noreferrer"
              className="ml-4 px-4 py-2 bg-indigo-600 hover:bg-indigo-700 rounded-md text-white font-semibold shadow-md transition"
              aria-label="Download Resume"
            >
              <FiDownload className="inline mr-1" /> Resume
            </a>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="pt-24 pb-16 max-w-6xl mx-auto px-6 md:px-12 space-y-16">
        {/* Profile Summary */}
        <section className="bg-white rounded-3xl shadow-lg p-10">
          <h2 className="text-indigo-700 text-3xl font-bold mb-6 flex items-center gap-3 select-none">
            👤 Profile Summary
          </h2>
          <p className="text-gray-700 leading-relaxed text-lg">
            Detail-oriented QA Tester with professional experience in manual testing of web and ad-tech platforms.
            Skilled in functional, regression, sanity, and smoke testing, validating ad integrations, and collaborating
            with developers for issue resolution. Strong theoretical and hands-on knowledge of Selenium automation and
            API testing through personal projects, committed to transitioning into automation roles.
          </p>
        </section>

        {/* Skills */}
        <section>
          <h2 className="text-indigo-700 text-3xl font-bold mb-10 flex items-center gap-3 select-none">
            🛠️ Skills
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            {Object.entries(skills).map(([category, items]) => (
              <div key={category}>
                <h3 className="text-indigo-600 text-xl font-semibold mb-5">{category}</h3>
                <ul className="list-disc list-inside space-y-2 text-gray-700">
                  {items.map((skill) => (
                    <li
                      key={skill}
                      className="hover:text-indigo-500 cursor-default transition-colors duration-300"
                    >
                      {skill}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </section>

        {/* Projects */}
        <section>
          <h2 className="text-indigo-700 text-3xl font-bold mb-12 flex items-center gap-3 select-none">
            🚀 Projects
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
            {projects.map(({ title, date, description, tech, repo, live }) => (
              <motion.article
                key={title}
                whileHover={{ scale: 1.05, boxShadow: "0 15px 30px rgba(99, 102, 241, 0.4)" }}
                className="bg-white rounded-3xl shadow-lg p-8 flex flex-col justify-between transition-shadow duration-300"
              >
                <div>
                  <h3 className="text-indigo-800 text-2xl font-semibold mb-1">{title}</h3>
                  <p className="text-indigo-500 italic mb-4">{date}</p>
                  <p className="text-gray-700 mb-6 leading-relaxed">{description}</p>
                </div>
                <div className="flex flex-wrap gap-3 mb-5">
                  {tech.map((t) => (
                    <span
                      key={t}
                      className="bg-indigo-100 text-indigo-800 px-4 py-1 rounded-full text-sm font-semibold cursor-default select-none shadow-sm"
                    >
                      {t}
                    </span>
                  ))}
                </div>
                <div className="flex gap-8">
                  {repo && (
                    <a
                      href={repo}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-700 font-semibold hover:underline flex items-center gap-2"
                    >
                      <FiGithub /> Repo
                    </a>
                  )}
                  {live && (
                    <a
                      href={live}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-700 font-semibold hover:underline flex items-center gap-2"
                    >
                      🔗 Live
                    </a>
                  )}
                </div>
              </motion.article>
            ))}
          </div>
        </section>

        {/* Footer */}
        <footer className="mt-20 border-t border-indigo-200 pt-10 flex flex-col md:flex-row justify-between items-center max-w-6xl mx-auto px-6 md:px-12">
          <p className="text-indigo-700 font-semibold select-none">Let's Collaborate 🤝</p>
          <div className="flex gap-8 text-indigo-700 text-3xl mt-4 md:mt-0">
            {contactLinks.map(({ icon, href, label }) => (
              <a
                key={label}
                href={href}
                target="_blank"
                rel="noopener noreferrer"
                aria-label={label}
                className="hover:text-indigo-500 transition-colors duration-300"
              >
                {icon}
              </a>
            ))}
          </div>
        </footer>
      </main>

      {/* Scroll to top button */}
      <AnimatePresence>
        {showScrollTop && (
          <motion.button
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            onClick={scrollToTop}
            aria-label="Scroll to top"
            className="fixed bottom-8 right-8 bg-indigo-700 hover:bg-indigo-800 text-white p-4 rounded-full shadow-lg z-50 transition-colors duration-300"
          >
            <FiArrowUp size={24} />
          </motion.button>
        )}
      </AnimatePresence>
    </>
  );
}
