import React, { useState } from "react";
import { motion } from "framer-motion";

// ---------------- AI Chatbot ----------------
function AIChatbot() {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState([
    { from: "bot", text: "Hi 👋, I'm Ajjayya AI! Ask me about my skills, projects, or resume." }
  ]);
  const [input, setInput] = useState("");

  const handleSend = () => {
    if (!input.trim()) return;
    const userMsg = { from: "user", text: input };
    setMessages((prev) => [...prev, userMsg]);

    let reply = "🤔 I don’t know that yet.";
    if (input.toLowerCase().includes("skill")) reply = "💡 I'm skilled in Selenium, Pytest, API Testing & React.js.";
    if (input.toLowerCase().includes("project")) reply = "🚀 My projects: Selenium Automation Framework & E-commerce site.";
    if (input.toLowerCase().includes("resume")) reply = "📄 You can download my resume from the top bar.";

    setMessages((prev) => [...prev, userMsg, { from: "bot", text: reply }]);
    setInput("");
  };

  return (
    <div>
      {open && (
        <motion.div 
          initial={{ scale: 0 }} 
          animate={{ scale: 1 }} 
          exit={{ scale: 0 }}
          className="fixed bottom-20 right-8 w-72 bg-white border rounded-xl shadow-xl p-4 flex flex-col z-50"
        >
          <div className="flex-1 overflow-y-auto space-y-2">
            {messages.map((m, i) => (
              <div key={i} className={m.from === "bot" ? "text-indigo-700" : "text-gray-800 text-right"}>
                <span className="block bg-indigo-100 px-2 py-1 rounded-lg inline-block">{m.text}</span>
              </div>
            ))}
          </div>
          <div className="flex mt-2">
            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              className="flex-1 border rounded-l px-2 py-1 text-sm"
              placeholder="Ask me..."
            />
            <button onClick={handleSend} className="bg-indigo-600 text-white px-3 rounded-r">Send</button>
          </div>
        </motion.div>
      )}
      <motion.button
        onClick={() => setOpen(!open)}
        className="fixed bottom-8 right-8 bg-indigo-600 hover:bg-indigo-700 text-white p-4 rounded-full shadow-lg z-50"
        whileHover={{ scale: 1.2 }}
      >
        🤖
      </motion.button>
    </div>
  );
}

// ---------------- Bug Hunt ----------------
function BugHunt({ onWin }) {
  const [score, setScore] = useState(0);

  const handleClick = () => {
    const newScore = score + 1;
    setScore(newScore);
    if (newScore === 3) onWin();
  };

  return (
    <motion.div
      onClick={handleClick}
      className="fixed bottom-32 left-10 cursor-pointer text-4xl select-none"
      title="Catch me!"
      animate={{ x: [0, 50, -50, 0], y: [0, -20, 20, 0] }}
      transition={{ repeat: Infinity, duration: 6 }}
    >
      🐞
    </motion.div>
  );
}

// ---------------- Test Demo ----------------
function TestDemo() {
  const [logs, setLogs] = useState([]);
  const testLogs = [
    "Running tests on E-commerce Project...",
    "✅ Login test passed",
    "✅ Add to cart test passed",
    "✅ Checkout flow passed",
    "🚀 All tests successful!"
  ];

  const runTests = async () => {
    setLogs([]);
    for (let i = 0; i < testLogs.length; i++) {
      await new Promise((r) => setTimeout(r, 1000));
      setLogs((prev) => [...prev, testLogs[i]]);
    }
  };

  return (
    <div className="mt-4">
      <button
        onClick={runTests}
        className="bg-indigo-600 text-white px-3 py-1 rounded shadow hover:bg-indigo-700"
      >
        ▶️ Run Tests
      </button>
      <div className="bg-black text-green-400 font-mono text-sm mt-2 p-2 rounded h-32 overflow-y-auto">
        {logs.map((line, i) => (
          <div key={i}>{line}</div>
        ))}
      </div>
    </div>
  );
}

// ---------------- Portfolio Main ----------------
export default function Portfolio() {
  const projects = [
    {
      title: "Selenium Hybrid Automation Framework",
      desc: "Hybrid framework using POM + Data-Driven approach with Jenkins CI/CD integration."
    },
    {
      title: "E-commerce Web App",
      desc: "Full-stack MERN project with cart, login, and payment system."
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900">
      {/* Header */}
      <header className="p-6 bg-indigo-600 text-white text-center shadow-lg">
        <h1 className="text-3xl font-bold">Ajjayya G V 🚀</h1>
        <p>QA Automation Engineer | Python | Selenium | React</p>
      </header>

      {/* Projects */}
      <section className="p-6">
        <h2 className="text-2xl font-semibold mb-4">Projects</h2>
        <div className="grid md:grid-cols-2 gap-6">
          {projects.map((p, i) => (
            <motion.div 
              key={i}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: i * 0.2 }}
              className="bg-white p-4 rounded-2xl shadow-md"
            >
              <h3 className="text-xl font-bold">{p.title}</h3>
              <p className="text-gray-600">{p.desc}</p>
              <TestDemo />
            </motion.div>
          ))}
        </div>
      </section>

      {/* Bug Hunt */}
      <BugHunt onWin={() => alert("🎉 You caught 3 bugs! Debugging master unlocked 🚀")} />

      {/* AI Chatbot */}
      <AIChatbot />
    </div>
  );
}
