import React, { useState, useEffect } from "react";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiSun,
  FiMoon,
} from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";

export default function App() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [darkMode]);

  const projects = [
    {
      title: "Selenium Hybrid Automation Framework",
      desc: "Hybrid framework using POM and Data-Driven approach with HTML reporting and Jenkins CI/CD integration.",
      repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
      tags: ["Python", "Selenium", "Pytest", "POM", "CI/CD"],
    },
    {
      title: "API Automation Framework",
      desc: "Automated REST API testing with endpoint validation, status code checks, and response verification.",
      repo: "https://github.com/iamajjayya/api-automation",
      tags: ["Python", "Pytest", "Requests", "API"],
    },
  ];

  const skills = [
    "Manual Testing",
    "Functional & Regression Testing",
    "Sanity & Smoke Testing",
    "Cross-Browser Testing",
    "Bug Reporting",
    "Selenium WebDriver",
    "Python",
    "Pytest",
    "API Testing",
    "SQL",
    "HTML/CSS/JavaScript",
    "React.js",
    "Postman",
    "Git/GitHub",
    "Jira",
    "Jenkins",
  ];

  return (
    <>
      <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-200 dark:from-gray-900 dark:to-gray-800 transition-colors duration-700">
        <div className="max-w-6xl mx-auto px-6 lg:px-12 py-12">
          {/* Dark mode toggle */}
          <div className="flex justify-end mb-6">
            <button
              aria-label="Toggle Dark Mode"
              onClick={() => setDarkMode((d) => !d)}
              className="p-2 rounded-full bg-indigo-600 text-white hover:bg-indigo-700 transition"
            >
              {darkMode ? <FiSun size={20} /> : <FiMoon size={20} />}
            </button>
          </div>

          {/* Header */}
          <motion.header
            initial={{ opacity: 0, y: -40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7 }}
            className="flex flex-col sm:flex-row items-start sm:items-center justify-between mb-12 gap-6"
          >
            <div>
              <h1 className="text-5xl font-extrabold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent select-text">
                Ajjayya G V â€” QA / Automation Tester
              </h1>
              <p className="mt-2 text-base text-gray-700 dark:text-gray-300 font-medium select-text">
                Electronic City, Bengaluru | DOB: 05-04-2002 | 6366325113
              </p>
            </div>
            <motion.a
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
              href="/resume.pdf"
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center gap-3 px-6 py-3 bg-gradient-to-r from-indigo-600 to-purple-600 text-white rounded-xl shadow-lg font-semibold tracking-wide shadow-indigo-400/50"
            >
              <FiDownload size={20} /> Resume
            </motion.a>
          </motion.header>

          {/* Profile Summary */}
          <motion.section
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2, duration: 0.6 }}
            className="backdrop-blur-md bg-white/80 dark:bg-gray-900/70 rounded-3xl p-8 shadow-lg mb-12"
          >
            <h2 className="text-2xl font-semibold mb-4 text-indigo-700 dark:text-indigo-400 select-text">
              Profile Summary
            </h2>
            <p className="text-gray-800 dark:text-gray-300 leading-relaxed text-lg max-w-4xl select-text">
              Detail-oriented QA Tester with professional experience in manual
              testing of web and ad-tech platforms. Skilled in executing
              functional, regression, sanity, and smoke testing, validating ad
              integrations, and collaborating with developers for issue
              resolution. Strong knowledge of Selenium automation and API
              testing through personal projects, with a commitment to
              transitioning into full automation roles.
            </p>

            <div className="mt-6 flex flex-wrap gap-3 max-w-4xl">
              {skills.map((s) => (
                <motion.span
                  key={s}
                  whileHover={{ scale: 1.15, backgroundColor: "#5c6ac4" }}
                  className="cursor-default select-none rounded-full px-4 py-2 bg-indigo-100 dark:bg-indigo-800 text-indigo-900 dark:text-indigo-200 text-sm font-semibold shadow-sm shadow-indigo-300 dark:shadow-indigo-900 transition-colors"
                >
                  {s}
                </motion.span>
              ))}
            </div>
          </motion.section>

          {/* Projects */}
          <section className="mb-12">
            <h3 className="text-3xl font-bold mb-8 text-indigo-700 dark:text-indigo-400 select-text">
              Projects
            </h3>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-8">
              {projects.map((p, i) => (
                <motion.article
                  key={p.title}
                  initial={{ opacity: 0, y: 30 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.3 + i * 0.15 }}
                  whileHover={{ scale: 1.05, boxShadow: "0 15px 25px rgba(99,102,241,0.5)" }}
                  className="bg-white dark:bg-gray-900 rounded-3xl p-7 shadow-lg cursor-pointer flex flex-col justify-between transition-shadow duration-300"
                >
                  <h4 className="font-semibold text-indigo-700 dark:text-indigo-400 text-xl mb-2 select-text">
                    {p.title}
                  </h4>
                  <p className="text-gray-700 dark:text-gray-300 text-base mb-4 select-text">
                    {p.desc}
                  </p>
                  <div className="flex flex-wrap items-center justify-between gap-3 mt-auto">
                    <div className="flex flex-wrap gap-2">
                      {p.tags.map((t) => (
                        <span
                          key={t}
                          className="text-xs px-3 py-1 rounded-full bg-indigo-100 dark:bg-indigo-800 text-indigo-700 dark:text-indigo-300 font-semibold select-text"
                        >
                          {t}
                        </span>
                      ))}
                    </div>
                    <a
                      href={p.repo}
                      target="_blank"
                      rel="noreferrer"
                      className="inline-flex items-center gap-2 text-indigo-600 dark:text-indigo-400 font-semibold hover:underline select-text"
                    >
                      View Repo <FiGithub size={18} />
                    </a>
                  </div>
                </motion.article>
              ))}
            </div>
          </section>

          {/* Footer */}
          <motion.footer
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.5 }}
            className="bg-white dark:bg-gray-900 rounded-3xl p-8 shadow-lg flex flex-col sm:flex-row items-center justify-between gap-6"
          >
            <div>
              <h4 className="font-semibold text-indigo-700 dark:text-indigo-400 select-text">
                Let's collaborate
              </h4>
              <p className="text-gray-700 dark:text-gray-300 text-sm max-w-md select-text">
                Manual & automation testing, framework design, CI/CD pipelines.
              </p>
            </div>
            <div className="flex gap-8 text-indigo-700 dark:text-indigo-400 text-lg">
              <a
                href="mailto:ajjayya2002@gmail.com"
                className="hover:text-indigo-500 dark:hover:text-indigo-300 transition"
                aria-label="Email"
              >
                <FiMail />
              </a>
              <a
                href="https://github.com/iamajjayya"
                target="_blank"
                rel="noreferrer"
                className="hover:text-indigo-500 dark:hover:text-indigo-300 transition"
                aria-label="GitHub"
              >
                <FiGithub />
              </a>
              <a
                href="https://linkedin.com/in/ajjayya-gv"
                target="_blank"
                rel="noreferrer"
                className="hover:text-indigo-500 dark:hover:text-indigo-300 transition"
                aria-label="LinkedIn"
              >
                <FiLinkedin />
              </a>
            </div>
          </motion.footer>
        </div>
      </div>
    </>
  );
}
