import React, { useEffect, useState } from "react";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiArrowUp,
} from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";
import { useInView } from "react-intersection-observer";

// Load Inter font via Google Fonts (include this in your index.html or import in CSS):
// <link href="https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap" rel="stylesheet" />

const contactLinks = [
  {
    icon: <FiMail />,
    href: "mailto:ajjayya2002@gmail.com",
    label: "Email",
  },
  {
    icon: <FiGithub />,
    href: "https://github.com/iamajjayya",
    label: "GitHub",
  },
  {
    icon: <FiLinkedin />,
    href: "https://linkedin.com/in/ajjayya-gv",
    label: "LinkedIn",
  },
];

// Bug quotes for tooltip
const bugQuotes = [
  "🐞 Keep calm and test on!",
  "🐛 Found a bug? Celebrate it 🎉",
  "🕷️ Bugs make us better testers!",
  "🔍 Testing: The art of breaking stuff",
  "⚙️ Automate all the things!",
  "🚀 Fast tests, faster feedback!",
  "🎯 Zero bugs? Dream on!",
  "📈 Quality matters, always!",
  "🔄 Regression testing is a life saver",
  "💡 Every bug is a lesson learned",
  "🧪 Test early, test often",
  "🛠️ Debugging like a pro",
  "📝 Test cases > guesswork",
  "💥 Break it till you make it!",
  "🎢 Testing: Rollercoaster ride of emotions",
  "🎉 Bugs found = victory dance time!",
  "🚧 Testing never sleeps",
  "🔧 Fix, test, repeat",
  "⏰ Time to automate, not procrastinate",
  "🧠 Test smart, not hard",
  "🌟 Every tester is a superhero",
  "💬 Bugs talk, do you listen?",
  "📚 Testers write the user manual",
  "⚡️ Quick tests, strong apps",
  "🎭 Testing uncovers the real face",
];

// Hook to manage random quote index with random timing between 5-9 sec
function useRandomQuote(quotes) {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    let timeoutId;
    function scheduleNext() {
      const delay = Math.floor(Math.random() * (9000 - 5000 + 1)) + 5000;
      timeoutId = setTimeout(() => {
        setIndex((prev) => (prev + 1) % quotes.length);
        scheduleNext();
      }, delay);
    }
    scheduleNext();

    return () => clearTimeout(timeoutId);
  }, [quotes.length]);

  return quotes[index];
}

// Bug component that floats and shows tooltip quote
function Bug({ size = 60 }) {
  const quote = useRandomQuote(bugQuotes);

  // Position states for moving bug
  const [pos, setPos] = useState({
    x: Math.random() * (window.innerWidth - size),
    y: Math.random() * (window.innerHeight - size),
  });

  useEffect(() => {
    let animationFrameId;
    let direction = { x: 1, y: 1 };
    let speed = 0.8 + Math.random() * 1.2;

    function move() {
      setPos((prev) => {
        let nextX = prev.x + direction.x * speed;
        let nextY = prev.y + direction.y * speed;

        // Bounce off edges
        if (nextX < 0 || nextX > window.innerWidth - size) direction.x *= -1;
        if (nextY < 0 || nextY > window.innerHeight - size) direction.y *= -1;

        return {
          x: Math.min(Math.max(nextX, 0), window.innerWidth - size),
          y: Math.min(Math.max(nextY, 0), window.innerHeight - size),
        };
      });

      animationFrameId = requestAnimationFrame(move);
    }

    animationFrameId = requestAnimationFrame(move);
    return () => cancelAnimationFrame(animationFrameId);
  }, [size]);

  return (
    <div
      style={{
        position: "fixed",
        top: pos.y,
        left: pos.x,
        width: size,
        height: size,
        borderRadius: "50%",
        background: "linear-gradient(135deg, #7c3aed, #4f46e5)",
        boxShadow: "0 0 15px rgba(124, 58, 237, 0.6)",
        cursor: "default",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        color: "white",
        fontSize: size / 2,
        userSelect: "none",
        zIndex: 9999,
      }}
      title={quote} // Tooltip on hover and always visible text below
    >
      🐞
      <div
        style={{
          position: "absolute",
          bottom: `calc(100% + 8px)`,
          left: "50%",
          transform: "translateX(-50%)",
          background: "rgba(124, 58, 237, 0.85)",
          color: "white",
          padding: "6px 10px",
          borderRadius: "12px",
          fontSize: 12,
          whiteSpace: "nowrap",
          pointerEvents: "none",
          userSelect: "none",
          boxShadow: "0 2px 8px rgba(0,0,0,0.3)",
          opacity: 0.9,
          fontWeight: "600",
          zIndex: 10000,
        }}
      >
        {quote}
      </div>
    </div>
  );
}

// Animate on scroll wrapper
function AnimatedSection({ children }) {
  const [ref, inView] = useInView({ triggerOnce: true, threshold: 0.2 });
  return (
    <motion.section
      ref={ref}
      initial={{ opacity: 0, y: 40 }}
      animate={inView ? { opacity: 1, y: 0 } : {}}
      transition={{ duration: 0.7, ease: "easeOut" }}
      className="mb-20"
    >
      {children}
    </motion.section>
  );
}

const skills = {
  Testing: [
    "📝 Manual Testing",
    "✔️ Functional Testing",
    "🔄 Regression Testing",
    "🔥 Sanity & Smoke Testing",
    "🌐 Cross-Browser Testing",
    "🐞 Bug Reporting",
    "📊 SDLC (Software Development Life Cycle)",
    "🛠️ STLC (Software Testing Life Cycle)",
    "🗒️ Test Case Design & Execution",
    "🔍 Defect Tracking & Reporting",
    "🧪 Exploratory Testing",
  ],
  Automation: [
    "🐍 Python",
    "🕷️ Selenium WebDriver",
    "🧪 Pytest",
    "📄 Page Object Model (POM)",
    "🌐 API Testing (Requests)",
    "⚙️ Jenkins CI/CD",
    "🔧 Git & GitHub",
    "🚀 Agile Methodology",
  ],
  Tools: [
    "🗄️ SQL",
    "💻 HTML, CSS, JavaScript",
    "⚛️ React.js",
    "📨 Postman",
    "📋 Jira",
  ],
};

const projects = [
  {
    title: "Selenium Hybrid Automation Framework",
    date: "May 2025 - Jun 2025",
    description:
      "Hybrid automation framework using Page Object Model & Data-Driven approach with HTML reports and Jenkins CI/CD integration.",
    tech: ["🐍 Python", "🕷️ Selenium", "🧪 Pytest", "📄 HTML Reports", "🛠️ Jenkins"],
    repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
  },
  {
    title: "API Automation Framework",
    date: "Aug 2025",
    description:
      "Automated REST API testing with validation of endpoints, status codes, and response content.",
    tech: ["🐍 Python", "🧪 Pytest", "🌐 Requests", "🛠️ Jenkins"],
    repo: "https://github.com/iamajjayya/api-automation",
  },
  {
    title: "E-commerce Website",
    date: "Live",
    description:
      "React.js e-commerce platform with user authentication, product listings, shopping cart, and checkout flow.",
    tech: ["⚛️ React.js", "🔐 Authentication", "🌐 REST API"],
    live: "https://ecommerceajjayya.netlify.app/",
  },
  {
    title: "Gym Application",
    date: "Live",
    description:
      "Workout guide app using ExerciseDB and YouTube APIs for exercise videos and routines.",
    tech: ["⚛️ React.js", "🌐 REST API", "▶️ YouTube API"],
    live: "https://your-gym-app-link.netlify.app/",
  },
];

const experience = [
  {
    role: "QA/QC Engineer",
    company: "RTBDemand.com",
    duration: "Sep 2024 – Present (Remote)",
    points: [
      "Performed manual testing for ad-tech systems on desktop and mobile.",
      "Verified ad script integration, refresh functionality, and placement.",
      "Executed sanity, smoke, and regression testing across multiple environments.",
      "Logged defects, created test documentation, collaborated with developers.",
      "Conducted basic API validations for ad delivery checks.",
    ],
  },
  {
    role: "Front-End Developer Intern",
    company: "Internplus+",
    duration: "Jul 2024 – Sep 2024 (Remote)",
    points: [
      "Built responsive UI components using React.js.",
      "Integrated and tested APIs in an Agile environment.",
    ],
  },
];

export default function Portfolio() {
  const [showScrollTop, setShowScrollTop] = useState(false);

  useEffect(() => {
    const onScroll = () => setShowScrollTop(window.scrollY > 300);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: "smooth" });

  return (
    <>
      {/* Header */}
      <header className="fixed top-0 left-0 right-0 bg-gradient-to-r from-indigo-400 to-purple-600 shadow-lg z-50 backdrop-blur-sm">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between px-6 py-4 gap-4 md:gap-0">
          <div className="flex items-center gap-3 select-none cursor-default">
            <motion.h1
              className="text-white text-3xl font-extrabold tracking-wide font-inter"
              whileHover={{ scale: 1.1, rotate: [0, 10, -10, 0] }}
              transition={{ duration: 1 }}
            >
              Ajjayya G V
            </motion.h1>
            <motion.span
              className="text-3xl"
              role="img"
              aria-label="waving robot"
              title="Hello! 👋"
              animate={{ rotate: [0, 20, -20, 0] }}
              transition={{ duration: 2, repeat: Infinity, repeatDelay: 2 }}
            >
              🤖
            </motion.span>
          </div>
          <p className="text-white font-medium tracking-wide md:ml-6 text-center md:text-left font-inter">
            QA Automation Tester | Python | Manual & Automation
          </p>
          <div className="flex items-center gap-6 text-white text-2xl justify-center md:justify-start">
            {contactLinks.map(({ icon, href, label }) => (
              <motion.a
                key={label}
                href={href}
                target="_blank"
                rel="noopener noreferrer"
                aria-label={label}
                whileHover={{
                  scale: 1.3,
                  color: "#facc15",
                  rotate: 10,
                  textShadow: "0 0 8px #facc15",
                }}
                whileTap={{ scale: 0.9, rotate: -10 }}
                transition={{ type: "spring", stiffness: 300 }}
                className="transition-colors"
              >
                {icon}
              </motion.a>
            ))}
            <a
              href="/resume.pdf"
              target="_blank"
              rel="noopener noreferrer"
              className="ml-4 px-5 py-2 bg-gradient-to-r from-yellow-400 to-yellow-600 hover:from-yellow-500 hover:to-yellow-700 rounded-md text-gray-900 font-semibold shadow-lg transition"
              aria-label="Download Resume"
            >
              <FiDownload className="inline mr-1" /> Resume
            </a>
          </div>
        </div>
      </header>

      <main className="pt-28 pb-16 max-w-6xl mx-auto px-6 md:px-12 font-inter">
        {/* Profile Summary */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-6 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            👤 Profile Summary
          </motion.h2>
          <p className="text-gray-700 leading-relaxed text-lg max-w-3xl">
            Detail-oriented QA Tester with professional experience in manual testing of web and ad-tech platforms.
            Skilled in functional, regression, sanity, and smoke testing, validating ad integrations, and collaborating
            with developers for issue resolution. Strong theoretical and hands-on knowledge of Selenium automation and
            API testing through personal projects, committed to transitioning into automation roles.
          </p>
        </AnimatedSection>

        {/* Skills */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-10 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            🛠️ Skills
          </motion.h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-10 max-w-5xl">
            {Object.entries(skills).map(([category, items]) => (
              <div key={category}>
                <h3 className="text-indigo-600 text-xl font-semibold mb-5">{category}</h3>
                <ul className="flex flex-wrap gap-3">
                  {items.map((skill) => (
                    <motion.li
                      key={skill}
                      whileHover={{
                        scale: 1.3,
                        rotate: [0, 10, -10, 0],
                        color: "#7c3aed",
                        textShadow: "0 0 8px #7c3aed",
                      }}
                      whileTap={{ scale: 0.9, rotate: 0 }}
                      className="bg-indigo-100 text-indigo-800 px-4 py-2 rounded-full text-sm font-semibold cursor-pointer select-none shadow-md transition-colors duration-300"
                    >
                      {skill}
                    </motion.li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </AnimatedSection>

        {/* Experience */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-10 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            💼 Professional Experience
          </motion.h2>
          <div className="space-y-10 max-w-4xl">
            {experience.map(({ role, company, duration, points }) => (
              <motion.article
                key={role}
                whileHover={{
                  scale: 1.05,
                  boxShadow: "0 10px 30px rgba(124, 58, 237, 0.4)",
                }}
                className="bg-white p-8 rounded-3xl shadow-md transition-shadow duration-300"
              >
                <h3 className="text-indigo-700 text-2xl font-semibold mb-1">{role}</h3>
                <p className="italic text-indigo-500 mb-4">
                  {company} • {duration}
                </p>
                <ul className="list-disc list-inside text-gray-700 space-y-2">
                  {points.map((point, i) => (
                    <li key={i}>{point}</li>
                  ))}
                </ul>
              </motion.article>
            ))}
          </div>
        </AnimatedSection>

        {/* Projects */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-12 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            🚀 Projects
          </motion.h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12 max-w-6xl">
            {projects.map(({ title, date, description, tech, repo, live }) => (
              <motion.article
                key={title}
                whileHover={{
                  scale: 1.07,
                  boxShadow: "0 15px 30px rgba(124, 58, 237, 0.5)",
                }}
                className="bg-white rounded-3xl shadow-lg p-8 flex flex-col justify-between transition-shadow duration-300"
              >
                <div>
                  <h3 className="text-indigo-800 text-2xl font-semibold mb-1">
                    {title}
                  </h3>
                  <p className="text-indigo-500 italic mb-4">{date}</p>
                  <p className="text-gray-700 mb-6 leading-relaxed">{description}</p>
                </div>
                <div className="flex flex-wrap gap-3 mb-5">
                  {tech.map((t) => (
                    <motion.span
                      key={t}
                      whileHover={{ scale: 1.4, rotate: [0, 10, -10, 0] }}
                      whileTap={{ scale: 0.9 }}
                      className="bg-indigo-100 text-indigo-800 px-4 py-1 rounded-full text-sm font-semibold cursor-pointer select-none shadow-sm"
                    >
                      {t}
                    </motion.span>
                  ))}
                </div>
                <div className="flex gap-8">
                  {repo && (
                    <a
                      href={repo}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-700 font-semibold hover:underline flex items-center gap-2"
                    >
                      <FiGithub /> Repo
                    </a>
                  )}
                  {live && (
                    <a
                      href={live}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-700 font-semibold hover:underline flex items-center gap-2"
                    >
                      🔗 Live
                    </a>
                  )}
                </div>
              </motion.article>
            ))}
          </div>
        </AnimatedSection>
      </main>

      {/* Scroll to top button */}
      <AnimatePresence>
        {showScrollTop && (
          <motion.button
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0, rotate: [0, 15, -15, 0] }}
            exit={{ opacity: 0, y: 20 }}
            onClick={scrollToTop}
            aria-label="Scroll to top"
            className="fixed bottom-8 right-8 bg-indigo-700 hover:bg-indigo-800 text-white p-4 rounded-full shadow-lg z-50 transition-colors duration-300"
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
          >
            <FiArrowUp size={24} />
          </motion.button>
        )}
      </AnimatePresence>

      {/* Bugs */}
      <Bug />
      <Bug />

      {/* Footer */}
      <footer className="mt-20 bg-indigo-50 py-8 text-center text-indigo-700 font-medium font-inter select-none">
        © {new Date().getFullYear()} Ajjayya G V — Reach Me Anytime!
      </footer>
    </>
  );
}
