import React, { useState, useEffect, useRef } from "react";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiArrowUp,
} from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";

const bugQuotes = [
  "üêû ‚ÄòIt‚Äôs not a bug, it‚Äôs an undocumented feature!‚Äô",
  "ü™≤ ‚ÄòDebugging: Where you dance with dragons.‚Äô",
  "üêõ ‚ÄòKeep calm and test on.‚Äô",
  "ü¶ü ‚ÄòCode never lies, tests sometimes do.‚Äô",
  "üêú ‚ÄòA bug in time saves nine!‚Äô",
  "üï∑Ô∏è ‚ÄòI love deadlines ‚Äî I like the whooshing sound they make as they fly by.‚Äô",
  "üêû ‚ÄòTesters: turning caffeine into bug reports since forever.‚Äô",
  "ü¶ó ‚ÄòBreaking things so you don‚Äôt have to.‚Äô",
  "üêù ‚ÄòGot bugs? We got logs.‚Äô",
  "ü™∞ ‚ÄòAutomate all the things!‚Äô",
  "ü¶† ‚ÄòTesting is my cardio.‚Äô",
  "üêû ‚ÄòNo bugs were harmed in the making of this app... yet.‚Äô",
  "üï∏Ô∏è ‚ÄòCatch me if you can ‚Äî I‚Äôm a sneaky bug!‚Äô",
  "ü¶ü ‚ÄòFinding bugs faster than you can say ‚ÄòQA‚Äô.‚Äô",
  "üêõ ‚ÄòTo err is human; to debug, divine.‚Äô",
  "üêú ‚ÄòEvery bug has a story.‚Äô",
  "üï∑Ô∏è ‚ÄòKeep calm and pass the tests.‚Äô",
  "üêù ‚ÄòMay your tests always pass, and your coffee never run out.‚Äô",
  "ü™∞ ‚ÄòOne bug to rule them all.‚Äô",
  "ü¶ó ‚ÄòHappy testing! Bugs not included.‚Äô",
  "üêû ‚ÄòEat, Sleep, Test, Repeat.‚Äô",
  "ü¶† ‚ÄòTesting is the art of breaking things gently.‚Äô",
  "ü¶ü ‚ÄòSquashing bugs, one line at a time.‚Äô",
  "üêõ ‚ÄòQA: Quality Awesomeness!‚Äô",
  "üï∏Ô∏è ‚ÄòBetter safe than sorry, test everything!‚Äô",
];

// Helper: random positions within viewport with some margin
const getRandomPosition = () => {
  const vw = window.innerWidth;
  const vh = window.innerHeight;
  // leave 10% margin on all sides
  return {
    left: Math.random() * (vw * 0.8) + vw * 0.1,
    top: Math.random() * (vh * 0.8) + vh * 0.1,
  };
};

const bugsCount = 4;

export default function Portfolio() {
  const [quoteIndexes, setQuoteIndexes] = useState(
    Array(bugsCount).fill(0).map(() => Math.floor(Math.random() * bugQuotes.length))
  );
  const [positions, setPositions] = useState(Array(bugsCount).fill(0).map(() => getRandomPosition()));
  const [showTop, setShowTop] = useState(false);
  const bugsRefs = useRef([]);

  // Rotate quotes every 15 seconds independently
  useEffect(() => {
    const intervals = quoteIndexes.map((_, i) =>
      setInterval(() => {
        setQuoteIndexes((prev) => {
          const newIndexes = [...prev];
          newIndexes[i] = (newIndexes[i] + 1) % bugQuotes.length;
          return newIndexes;
        });
      }, 15000 + i * 4000) // staggered rotation
    );
    return () => intervals.forEach(clearInterval);
  }, []);

  // Slow floating movement for bugs
  useEffect(() => {
    let animationFrameId;
    const speed = 0.15; // px per frame approx
    const direction = Array(bugsCount).fill(0).map(() => ({
      x: Math.random() < 0.5 ? -speed : speed,
      y: Math.random() < 0.5 ? -speed : speed,
    }));

    const animate = () => {
      setPositions((prevPositions) =>
        prevPositions.map((pos, i) => {
          let newX = pos.left + direction[i].x;
          let newY = pos.top + direction[i].y;

          const vw = window.innerWidth;
          const vh = window.innerHeight;
          // Bounce off edges (with 50px padding)
          if (newX < 50 || newX > vw - 50) direction[i].x = -direction[i].x;
          if (newY < 50 || newY > vh - 50) direction[i].y = -direction[i].y;

          newX = Math.min(Math.max(newX, 50), vw - 50);
          newY = Math.min(Math.max(newY, 50), vh - 50);

          return { left: newX, top: newY };
        })
      );
      animationFrameId = requestAnimationFrame(animate);
    };
    animationFrameId = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrameId);
  }, []);

  // Show scroll-to-top button after scroll 300px
  useEffect(() => {
    const onScroll = () => {
      setShowTop(window.scrollY > 300);
    };
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  // Scroll up on button click
  const scrollToTop = () =>
    window.scrollTo({ top: 0, behavior: "smooth" });

  // Skills grouped + emojis + simple hover color change
  const skillsGroups = [
    {
      title: "üß™ Testing",
      skills: [
        "Manual Testing",
        "Functional Testing",
        "Regression Testing",
        "Sanity & Smoke Testing",
        "Cross-Browser Testing",
        "Bug Reporting",
        "SDLC",
        "STLC",
        "Agile",
      ],
    },
    {
      title: "ü§ñ Automation",
      skills: [
        "Selenium WebDriver",
        "Python",
        "Pytest",
        "Page Object Model (POM)",
        "API Testing with Requests",
        "HTML Reports",
      ],
    },
    {
      title: "üíª Technical",
      skills: [
        "SQL",
        "HTML",
        "CSS",
        "JavaScript",
        "React.js",
        "REST API",
      ],
    },
    {
      title: "üõ† Tools",
      skills: ["Postman", "Git", "GitHub", "Jira", "Jenkins"],
    },
  ];

  const projects = [
    {
      title: "Selenium Hybrid Automation Framework",
      desc: "Hybrid framework using POM and Data-Driven approach with HTML reporting and Jenkins CI/CD integration.",
      repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
      tags: ["Python", "Selenium", "Pytest", "POM", "CI/CD"],
    },
    {
      title: "API Automation Framework",
      desc: "Automated REST API testing with validation, status code checks, and response verification.",
      repo: "https://github.com/iamajjayya/api-automation",
      tags: ["Python", "Pytest", "Requests", "API"],
    },
    {
      title: "Gym Application",
      desc: "Workout guide using ExerciseDB API & YouTube API.",
      repo: "https://github.com/iamajjayya/gym-app",
      live: "https://gym-app-ajjayya.netlify.app/",
      tags: ["React.js", "REST API", "YouTube API"],
    },
    {
      title: "E-commerce Website",
      desc: "React-based e-commerce platform with user authentication, product listings, cart & checkout.",
      repo: "https://github.com/iamajjayya/ecommerce-site",
      live: "https://ecommerceajjayya.netlify.app/",
      tags: ["React.js", "Auth", "Shopping Cart"],
    },
  ];

  const experience = [
    {
      role: "QA Tester",
      company: "RTBDemand.com",
      period: "Sep 2024 ‚Äì Present",
      location: "Remote",
      details: [
        "Performed manual testing for ad-tech systems on desktop and mobile.",
        "Verified ad script integration, refresh functionality, and placement.",
        "Executed sanity, smoke, and regression testing across multiple environments.",
        "Logged defects, created test documentation, and collaborated with developers.",
        "Conducted basic API validations for ad delivery checks.",
      ],
    },
    {
      role: "Front-End Developer Intern",
      company: "Internplus+",
      period: "Jul 2024 ‚Äì Sep 2024",
      location: "Remote",
      details: [
        "Built responsive UI components using React.js.",
        "Integrated and tested APIs in an Agile environment.",
      ],
    },
  ];

  return (
    <div
      className="min-h-screen relative bg-gradient-to-tr from-indigo-900 via-indigo-700 to-purple-800 text-white font-sans"
      style={{ scrollBehavior: "smooth" }}
    >
      {/* Sticky Header */}
      <header className="fixed top-0 left-0 w-full bg-indigo-900/90 backdrop-blur-md z-50 shadow-lg">
        <div className="max-w-6xl mx-auto flex flex-wrap items-center justify-between px-6 py-4 gap-4">
          <h1 className="text-xl font-extrabold tracking-wide select-none">
            Ajjayya G V ‚Äî QA / Automation Tester
          </h1>
          <nav className="flex gap-6 text-indigo-300 text-sm">
            <a
              href="mailto:ajjayya2002@gmail.com"
              className="hover:text-indigo-50 flex items-center gap-1"
              aria-label="Email"
            >
              <FiMail /> ajjayya2002@gmail.com
            </a>
            <a
              href="https://linkedin.com/in/ajjayya-gv"
              target="_blank"
              rel="noreferrer"
              className="hover:text-indigo-50 flex items-center gap-1"
              aria-label="LinkedIn"
            >
              <FiLinkedin /> LinkedIn
            </a>
            <a
              href="https://github.com/iamajjayya"
              target="_blank"
              rel="noreferrer"
              className="hover:text-indigo-50 flex items-center gap-1"
              aria-label="GitHub"
            >
              <FiGithub /> GitHub
            </a>
            <a
              href="/resume.pdf"
              download
              className="bg-indigo-600 hover:bg-indigo-700 transition rounded-md px-3 py-1 flex items-center gap-2 shadow"
            >
              <FiDownload /> Resume
            </a>
          </nav>
        </div>
      </header>

      {/* Spacer for header */}
      <div className="h-16"></div>

      {/* Main Content Container */}
      <main className="max-w-6xl mx-auto px-6 sm:px-8 md:px-12 lg:px-16 pb-16 space-y-12">
        {/* Profile Summary */}
        <section
          id="profile"
          className="bg-indigo-800/60 rounded-xl p-6 shadow-lg backdrop-blur-sm border border-indigo-600"
        >
          <h2 className="text-3xl font-semibold mb-4 flex items-center gap-3 select-none">
            üë§ Profile Summary
          </h2>
          <p className="text-indigo-200 leading-relaxed max-w-4xl">
            Detail-oriented QA Tester with 1+ year of hands-on manual testing
            experience on web & ad-tech platforms. Skilled in functional,
            regression, sanity, smoke, and cross-browser testing. Strong
            collaboration with developers to resolve issues. Passionate about
            Python automation ‚Äî personally skilled in Selenium WebDriver,
            Pytest, API testing, and framework design. Committed to delivering
            high-quality software with reliability and performance focus.
          </p>
        </section>

        {/* Skills */}
        <section
          id="skills"
          className="space-y-8"
          aria-label="Skills Section"
          tabIndex={-1}
        >
          <h2 className="text-3xl font-semibold mb-6 select-none flex items-center gap-3">
            ‚öôÔ∏è Skills
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
            {skillsGroups.map(({ title, skills }) => (
              <div
                key={title}
                className="bg-indigo-800/60 border border-indigo-600 rounded-xl p-6 shadow-lg hover:shadow-xl transition cursor-default"
              >
                <h3 className="text-xl mb-4 font-semibold flex items-center gap-2 select-none">
                  <span className="text-indigo-400">{title}</span>
                </h3>
                <ul className="space-y-2 text-indigo-200">
                  {skills.map((skill) => (
                    <li
                      key={skill}
                      className="hover:text-indigo-50 transition cursor-default"
                    >
                      ‚Ä¢ {skill}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </section>

        {/* Projects */}
        <section
          id="projects"
          className="space-y-8"
          aria-label="Projects Section"
          tabIndex={-1}
        >
          <h2 className="text-3xl font-semibold select-none flex items-center gap-3">
            üìÅ Projects
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {projects.map(({ title, desc, repo, live, tags }) => (
              <motion.article
                key={title}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                whileHover={{ scale: 1.03 }}
                transition={{ duration: 0.3 }}
                className="bg-indigo-800/60 border border-indigo-600 rounded-xl p-6 shadow-lg cursor-pointer"
              >
                <h3 className="text-xl font-semibold text-indigo-300 mb-3 select-none">
                  {title}
                </h3>
                <p className="text-indigo-200 mb-4">{desc}</p>
                <div className="flex flex-wrap gap-2 mb-4">
                  {tags.map((tag) => (
                    <span
                      key={tag}
                      className="text-xs font-medium px-3 py-1 rounded-full bg-indigo-700 hover:bg-indigo-600 transition"
                    >
                      {tag}
                    </span>
                  ))}
                </div>
                <div className="flex gap-4 items-center text-indigo-300">
                  <a
                    href={repo}
                    target="_blank"
                    rel="noreferrer"
                    className="hover:text-indigo-50 underline"
                  >
                    GitHub Repo
                  </a>
                  {live && (
                    <a
                      href={live}
                      target="_blank"
                      rel="noreferrer"
                      className="hover:text-indigo-50 underline"
                    >
                      Live Demo
                    </a>
                  )}
                </div>
              </motion.article>
            ))}
          </div>
        </section>

        {/* Experience */}
        <section
          id="experience"
          className="space-y-8"
          aria-label="Experience Section"
          tabIndex={-1}
        >
          <h2 className="text-3xl font-semibold mb-6 select-none flex items-center gap-3">
            üíº Experience
          </h2>
          <div className="space-y-6 max-w-4xl">
            {experience.map(({ role, company, period, location, details }) => (
              <motion.article
                key={company + role}
                initial={{ opacity: 0, y: 15 }}
                animate={{ opacity: 1, y: 0 }}
                whileHover={{ scale: 1.02 }}
                transition={{ duration: 0.3 }}
                className="bg-indigo-800/60 border border-indigo-600 rounded-xl p-6 shadow-lg"
              >
                <h3 className="font-semibold text-indigo-300 mb-1 select-none">
                  {role} @ {company}
                </h3>
                <p className="text-indigo-400 mb-2 italic">
                  {period} | {location}
                </p>
                <ul className="list-disc list-inside text-indigo-200 space-y-1">
                  {details.map((item, idx) => (
                    <li key={idx}>{item}</li>
                  ))}
                </ul>
              </motion.article>
            ))}
          </div>
        </section>
      </main>

      {/* Floating Bugs */}
      {positions.map((pos, i) => (
        <motion.div
          key={i}
          ref={(el) => (bugsRefs.current[i] = el)}
          className="fixed cursor-pointer select-none z-40"
          style={{ left: pos.left, top: pos.top, fontSize: "2.6rem" }}
          initial={{ rotate: 0 }}
          whileHover={{ scale: 1.3, rotate: 360 }}
          transition={{ type: "spring", stiffness: 300, damping: 20 }}
          onClick={() =>
            setQuoteIndexes((prev) => {
              const copy = [...prev];
              copy[i] = (copy[i] + 1) % bugQuotes.length;
              return copy;
            })
          }
          aria-label="Funny QA bug"
          title={bugQuotes[quoteIndexes[i]]}
        >
          üêû
          {/* Tooltip */}
          <AnimatePresence>
            <motion.div
              key={quoteIndexes[i]}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 0.95, y: 0 }}
              exit={{ opacity: 0, y: 10 }}
              transition={{ duration: 0.3 }}
              className="absolute -top-10 left-1/2 transform -translate-x-1/2 px-4 py-1 rounded-md bg-indigo-900/95 text-xs text-indigo-50 whitespace-nowrap select-text pointer-events-none"
            >
              {bugQuotes[quoteIndexes[i]]}
            </motion.div>
          </AnimatePresence>
        </motion.div>
      ))}

      {/* Scroll to Top */}
      <AnimatePresence>
        {showTop && (
          <motion.button
            onClick={scrollToTop}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            className="fixed bottom-6 right-6 z-50 rounded-full bg-indigo-700 hover:bg-indigo-600 shadow-lg p-3 text-white cursor-pointer"
            aria-label="Scroll to top"
            title="Scroll to Top"
          >
            <FiArrowUp size={24} />
          </motion.button>
        )}
      </AnimatePresence>

      {/* Footer */}
      <footer className="bg-indigo-900/90 backdrop-blur-md text-indigo-300 py-4 text-center text-sm select-none">
        ¬© 2025 Ajjayya G V ‚Ä¢ QA & Automation Tester ‚Ä¢ Crafted with ‚ù§Ô∏è & üêû Bugs
      </footer>
    </div>
  );
}
