import React, { useEffect, useState } from "react";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiArrowUp,
} from "react-icons/fi";
import { motion, AnimatePresence } from "framer-motion";
import { useInView } from "react-intersection-observer";

// Contact links
const contactLinks = [
  { icon: <FiMail />, href: "mailto:ajjayya2002@gmail.com", label: "Email" },
  { icon: <FiGithub />, href: "https://github.com/iamajjayya", label: "GitHub" },
  { icon: <FiLinkedin />, href: "https://linkedin.com/in/ajjayya-gv", label: "LinkedIn" },
];

// Skills categorized with emojis
const skills = {
  Testing: [
    "📝 Manual Testing",
    "✔️ Functional Testing",
    "🔄 Regression Testing",
    "🔥 Sanity & Smoke Testing",
    "🌐 Cross-Browser Testing",
    "🐞 Bug Reporting",
    "📊 SDLC",
    "🛠️ STLC",
    "🗒️ Test Case Design",
    "🔍 Defect Tracking",
    "🧪 Exploratory Testing",
  ],
  Automation: [
    "🐍 Python",
    "🕷️ Selenium WebDriver",
    "🧪 Pytest",
    "📄 Page Object Model (POM)",
    "🌐 API Testing",
    "⚙️ Jenkins CI/CD",
    "🔧 Git & GitHub",
    "🚀 Agile Methodology",
  ],
  Tools: [
    "🗄️ SQL",
    "💻 HTML, CSS, JavaScript",
    "⚛️ React.js",
    "📨 Postman",
    "📋 Jira",
  ],
};

// Projects array
const projects = [
  {
    title: "Selenium Hybrid Automation Framework",
    date: "May 2025 - Jun 2025",
    description:
      "Hybrid automation framework using POM & Data-Driven approach with HTML reports and Jenkins CI/CD integration.",
    tech: ["🐍 Python", "🕷️ Selenium", "🧪 Pytest", "📄 HTML Reports", "🛠️ Jenkins"],
    repo: "https://github.com/iamajjayya/selenium-hybrid-framework",
  },
  {
    title: "API Automation Framework",
    date: "Aug 2025",
    description:
      "Automated REST API testing with validation of endpoints, status codes, and response content.",
    tech: ["🐍 Python", "🧪 Pytest", "🌐 Requests", "🛠️ Jenkins"],
    repo: "https://github.com/iamajjayya/api-automation",
  },
  {
    title: "E-commerce Website",
    date: "Live",
    description:
      "React.js e-commerce platform with user authentication, product listings, shopping cart, and checkout flow.",
    tech: ["⚛️ React.js", "🔐 Authentication", "🌐 REST API"],
    live: "https://ecommerceajjayya.netlify.app/",
  },
  {
    title: "Gym Application",
    date: "Live",
    description:
      "Workout guide app using ExerciseDB and YouTube APIs for exercise videos and routines.",
    tech: ["⚛️ React.js", "🌐 REST API", "▶️ YouTube API"],
    live: "https://your-gym-app-link.netlify.app/",
  },
];

// Experience array
const experience = [
  {
    role: "QA/QC Engineer",
    company: "RTBDemand.com",
    duration: "Sep 2024 – Present (Remote)",
    points: [
      "Performed manual testing for ad-tech systems on desktop and mobile.",
      "Verified ad script integration, refresh functionality, and placement.",
      "Executed sanity, smoke, and regression testing across multiple environments.",
      "Logged defects, created test documentation, collaborated with developers.",
      "Conducted basic API validations for ad delivery checks.",
    ],
  },
  {
    role: "Front-End Developer Intern",
    company: "Internplus+",
    duration: "Jul 2024 – Sep 2024 (Remote)",
    points: [
      "Built responsive UI components using React.js.",
      "Integrated and tested APIs in an Agile environment.",
    ],
  },
];

// Funny bug quotes to show on tooltips
const bugQuotes = [
  "🐞 'I’m not a bug, I’m a feature!'",
  "🐛 'Debugging: where you stare at code until it confesses.'",
  "🪲 'Why did the tester go broke? Because he found too many bugs!'",
  "🐞 'Keep calm and squash bugs.'",
  "🐛 'This bug’s harder to catch than my morning coffee!'",
  "🪲 'I put the ‘test’ in ‘detest bugs.’'",
  "🐞 '99 little bugs in the code... Take one down, patch it around.'",
  "🐛 'If debugging is the process of removing bugs, then programming must be the process of putting them in.'",
  "🪲 'Bugs are like cats — they come back even when you don’t want them.'",
  "🐞 'Test early, test often, and test with a smile.'",
  "🐛 'Automate all the tests, so I can automate my coffee break!'",
  "🪲 'Bug reports: The user’s way of saying hello.'",
  "🐞 'I love deadlines. Especially the whooshing sound they make as they fly by.'",
  "🐛 'Code is like humor. When you have to explain it, it’s bad.'",
  "🪲 'To err is human; to debug, divine.'",
  "🐞 'My code doesn’t have bugs. It just develops random features.'",
  "🐛 'Trust me, I’m a QA tester — I break things professionally!'",
  "🪲 'A good tester is part detective, part magician.'",
  "🐞 'First rule of debugging: If it works, don’t touch it!'",
  "🐛 'Breaking code and making it better — my daily ritual.'",
  "🪲 'The bug stops here!'",
  "🐞 'Eat, sleep, test, repeat.'",
  "🐛 'I break it, you fix it — team player.'",
  "🪲 'Life’s too short for manual regression tests.'",
  "🐞 'QA: Because someone’s got to catch the mess!'",
];

// Bug component floating with tooltip always visible
function Bug({ quote, style, id }) {
  // Animate floating and rotating bugs
  return (
    <motion.div
      layoutId={id}
      style={style}
      className="fixed cursor-pointer select-none"
      initial={{ opacity: 0.7 }}
      animate={{
        y: [0, -15, 0],
        rotate: [0, 15, -15, 0],
      }}
      transition={{
        duration: 5 + Math.random() * 5,
        repeat: Infinity,
        ease: "easeInOut",
        delay: Math.random() * 5,
      }}
      whileHover={{ scale: 1.3, zIndex: 1000 }}
      onClick={() => {
        // Play a fun bounce animation on click
      }}
    >
      <div className="relative group">
        <span className="text-3xl" role="img" aria-label="bug">
          🐞
        </span>
        {/* Tooltip always visible */}
        <div className="absolute -top-10 left-1/2 transform -translate-x-1/2 whitespace-nowrap rounded-md bg-indigo-600 text-white px-3 py-1 text-sm font-semibold shadow-lg select-none pointer-events-none opacity-90 z-50">
          {quote}
        </div>
      </div>
    </motion.div>
  );
}

// Animate section on scroll
function AnimatedSection({ children }) {
  const [ref, inView] = useInView({ triggerOnce: true, threshold: 0.15 });
  return (
    <motion.section
      ref={ref}
      initial={{ opacity: 0, y: 40 }}
      animate={inView ? { opacity: 1, y: 0 } : {}}
      transition={{ duration: 0.7, ease: "easeOut" }}
      className="mb-20"
    >
      {children}
    </motion.section>
  );
}

export default function Portfolio() {
  const [showScrollTop, setShowScrollTop] = useState(false);

  useEffect(() => {
    const onScroll = () => setShowScrollTop(window.scrollY > 300);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: "smooth" });

  // Random positions for bugs on screen for desktop and mobile
  // Using vw and vh so they stay relative to screen size
  const bugPositions = [
    { top: "15vh", left: "10vw" },
    { top: "40vh", left: "80vw" },
    { top: "70vh", left: "35vw" },
    { top: "25vh", left: "60vw" },
  ];

  return (
    <>
      {/* Header */}
      <header className="fixed top-0 left-0 right-0 bg-gradient-to-r from-purple-700 via-indigo-800 to-blue-700 shadow-lg z-50 backdrop-blur-md">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between px-6 py-4 gap-4 md:gap-0">
          <div className="flex items-center gap-3 select-none cursor-default">
            <motion.h1
              className="text-white text-3xl font-extrabold tracking-wide"
              whileHover={{ scale: 1.1, rotate: [0, 10, -10, 0] }}
              transition={{ duration: 1 }}
            >
              Ajjayya G V
            </motion.h1>
            <motion.span
              className="text-3xl"
              role="img"
              aria-label="waving robot"
              title="Hello! 👋"
              animate={{ rotate: [0, 20, -20, 0] }}
              transition={{ duration: 2, repeat: Infinity, repeatDelay: 2 }}
            >
              🤖
            </motion.span>
          </div>
          <p className="text-indigo-200 font-medium tracking-wide md:ml-6 text-center md:text-left">
            QA Automation Tester | Python | Manual & Automation
          </p>
          <div className="flex items-center gap-6 text-white text-2xl justify-center md:justify-start">
            {contactLinks.map(({ icon, href, label }) => (
              <motion.a
                key={label}
                href={href}
                target="_blank"
                rel="noopener noreferrer"
                aria-label={label}
                whileHover={{ scale: 1.3, color: "#c4b5fd", rotate: 10 }}
                whileTap={{ scale: 0.9, rotate: -10 }}
                transition={{ type: "spring", stiffness: 300 }}
                className="transition-colors"
              >
                {icon}
              </motion.a>
            ))}
            <a
              href="/resume.pdf"
              target="_blank"
              rel="noopener noreferrer"
              className="ml-4 px-4 py-2 bg-gradient-to-r from-indigo-600 to-purple-700 hover:from-indigo-700 hover:to-purple-800 rounded-md text-white font-semibold shadow-md transition"
              aria-label="Download Resume"
            >
              <FiDownload className="inline mr-1" /> Resume
            </a>
          </div>
        </div>
      </header>

      {/* Bugs floating */}
      {bugQuotes.slice(0, 4).map((quote, i) => (
        <Bug
          key={i}
          quote={quote}
          style={{ top: bugPositions[i].top, left: bugPositions[i].left }}
          id={`bug-${i}`}
        />
      ))}

      {/* Main content */}
      <main className="pt-28 pb-16 max-w-6xl mx-auto px-6 md:px-12">
        {/* Profile Summary */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-6 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            👤 Profile Summary
          </motion.h2>
          <p className="text-gray-700 leading-relaxed text-lg">
            Detail-oriented QA Tester with professional experience in manual testing of web and ad-tech platforms.
            Skilled in functional, regression, sanity, and smoke testing, validating ad integrations, and collaborating
            with developers for issue resolution. Strong theoretical and hands-on knowledge of Selenium automation and
            API testing through personal projects, committed to transitioning into automation roles.
          </p>
        </AnimatedSection>

        {/* Skills */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-10 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            🛠️ Skills
          </motion.h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            {Object.entries(skills).map(([category, items]) => (
              <div key={category}>
                <h3 className="text-indigo-600 text-xl font-semibold mb-5">{category}</h3>
                <ul className="flex flex-wrap gap-3">
                  {items.map((skill) => (
                    <motion.li
                      key={skill}
                      whileHover={{
                        scale: 1.3,
                        rotate: [0, 10, -10, 0],
                        color: "#7c3aed",
                        textShadow: "0 0 8px #7c3aed",
                      }}
                      whileTap={{ scale: 0.9, rotate: 0 }}
                      className="bg-indigo-100 text-indigo-800 px-4 py-2 rounded-full text-sm font-semibold cursor-pointer select-none shadow-md transition-colors duration-300"
                    >
                      {skill}
                    </motion.li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </AnimatedSection>

        {/* Experience */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-10 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            💼 Professional Experience
          </motion.h2>
          <div className="space-y-10">
            {experience.map(({ role, company, duration, points }) => (
              <motion.article
                key={role}
                whileHover={{
                  scale: 1.05,
                  boxShadow: "0 10px 30px rgba(124, 58, 237, 0.4)",
                }}
                className="bg-white p-8 rounded-3xl shadow-md transition-shadow duration-300"
              >
                <h3 className="text-indigo-700 text-2xl font-semibold mb-1">{role}</h3>
                <p className="italic text-indigo-500 mb-4">
                  {company} • {duration}
                </p>
                <ul className="list-disc list-inside text-gray-700 space-y-2">
                  {points.map((point, i) => (
                    <li key={i}>{point}</li>
                  ))}
                </ul>
              </motion.article>
            ))}
          </div>
        </AnimatedSection>

        {/* Projects */}
        <AnimatedSection>
          <motion.h2
            className="text-indigo-700 text-3xl font-bold mb-12 flex items-center gap-3 select-none cursor-default"
            whileHover={{ scale: 1.1, rotate: [0, 15, -15, 0] }}
            transition={{ duration: 0.8 }}
          >
            🚀 Projects
          </motion.h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
            {projects.map(({ title, date, description, tech, repo, live }) => (
              <motion.article
                key={title}
                whileHover={{
                  scale: 1.07,
                  boxShadow: "0 15px 30px rgba(124, 58, 237, 0.5)",
                }}
                className="bg-white rounded-3xl shadow-lg p-8 flex flex-col justify-between transition-shadow duration-300"
              >
                <div>
                  <h3 className="text-indigo-800 text-2xl font-semibold mb-1">
                    {title}
                  </h3>
                  <p className="text-indigo-500 italic mb-4">{date}</p>
                  <p className="text-gray-700 mb-6 leading-relaxed">{description}</p>
                </div>
                <div className="flex flex-wrap gap-3 mb-5">
                  {tech.map((t) => (
                    <motion.span
                      key={t}
                      whileHover={{ scale: 1.4, rotate: [0, 10, -10, 0] }}
                      whileTap={{ scale: 0.9 }}
                      className="bg-indigo-100 text-indigo-800 px-4 py-1 rounded-full text-sm font-semibold cursor-pointer select-none shadow-sm"
                    >
                      {t}
                    </motion.span>
                  ))}
                </div>
                <div className="flex gap-8">
                  {repo && (
                    <a
                      href={repo}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-700 font-semibold hover:underline flex items-center gap-2"
                    >
                      <FiGithub /> Repo
                    </a>
                  )}
                  {live && (
                    <a
                      href={live}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-700 font-semibold hover:underline flex items-center gap-2"
                    >
                      🔗 Live
                    </a>
                  )}
                </div>
              </motion.article>
            ))}
          </div>
        </AnimatedSection>
      </main>

      {/* Scroll to top button */}
      <AnimatePresence>
        {showScrollTop && (
          <motion.button
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0, rotate: [0, 15, -15, 0] }}
            exit={{ opacity: 0, y: 20 }}
            onClick={scrollToTop}
            aria-label="Scroll to top"
            className="fixed bottom-8 right-8 bg-gradient-to-r from-indigo-600 to-purple-700 hover:from-indigo-700 hover:to-purple-800 text-white p-4 rounded-full shadow-lg z-50 transition-colors duration-300"
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
          >
            <FiArrowUp size={24} />
          </motion.button>
        )}
      </AnimatePresence>
    </>
  );
}
