import React, { useState, useEffect } from "react";
import {
  FiDownload,
  FiMail,
  FiGithub,
  FiLinkedin,
  FiSun,
  FiMoon,
} from "react-icons/fi";
import { motion } from "framer-motion";

export default function App() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [darkMode]);

  const projects = [
    {
      title: "API Automation Framework",
      period: "Aug 2025 - Aug 2025",
      descPoints: [
        "Automated FakeStore API endpoints validation.",
        "Schema validation to ensure correct response structure.",
        "Positive and negative test cases for robust coverage.",
        "Status code and response content verification.",
        "Built with Python, pytest, and requests library.",
        "Data-Driven Testing (DDT) using CSV and JSON files.",
