import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FiDownload, FiMail, FiGithub, FiLinkedin, FiArrowUp } from "react-icons/fi";

// Ladybug emoji positions
const ladybugPositions = [
  { top: "20%", left: "5%" },
  { top: "40%", right: "10%" },
  { bottom: "25%", left: "15%" },
  { bottom: "10%", right: "20%" },
];

// Funny, testing-related ladybug quotes
const bugQuotes = [
  "🐞 ‘Why did the tester cross the road? To find a bug on the other side!’ 😂",
  "🧹 ‘Squashing bugs like a pro — one click at a time!’",
  "🛠️ ‘Testers don’t break software, they expose its secrets!’ 🤫",
  "💡 ‘Automate all the tests... so you can take coffee breaks!’ ☕",
  "🔍 ‘Finding bugs faster than a speeding bullet!’ 🦸‍♂️",
];

// Experience data with corrected role
const experience = [
